import React, { useState, useEffect, useCallback, useMemo } from 'react';
import axios from 'axios';
import { LogIn, User, BookOpen, Settings, Home, Plus, Trash2, RotateCcw, Save } from 'lucide-react';

// Configure Axios to point to the Flask backend
const API_BASE_URL = 'http://localhost:5001/api'; // Đảm bảo port này khớp với backend Flask
axios.defaults.baseURL = API_BASE_URL;
axios.defaults.headers.common['Content-Type'] = 'application/json';

// --- Global Setup for Tailwind CDN ---
(function() {
  if (!document.getElementById('tailwind-cdn')) {
    const script = document.createElement('script');
    script.src = 'https://cdn.tailwindcss.com';
    script.id = 'tailwind-cdn';
    document.head.appendChild(script);
  }
})();

// --- Utility Functions and Hooks ---

// Hook để quản lý trạng thái global của ứng dụng
const useAppState = () => {
    // State cho user, khởi tạo từ localStorage
    const [user, setUser] = useState(() => {
        try {
            const savedUser = localStorage.getItem('user_data');
            // Xử lý lỗi parse JSON nếu dữ liệu trong localStorage bị hỏng hoặc null/undefined
            return savedUser && savedUser !== 'undefined' && savedUser !== 'null' ? JSON.parse(savedUser) : null;
        } catch (e) {
            console.error("Lỗi parse user_data từ localStorage", e);
            localStorage.removeItem('user_data'); // Xóa dữ liệu lỗi
            return null;
        }
    });
    // State cho token, khởi tạo từ localStorage
    const [token, setToken] = useState(() => {
      const storedToken = localStorage.getItem('jwt_token');
      // Đảm bảo token không phải là null hoặc chuỗi "null"/"undefined"
      return (storedToken && storedToken !== 'null' && storedToken !== 'undefined') ? storedToken : null;
    });
    // State cho route hiện tại
    const [route, setRoute] = useState('home');
    // State cho thông báo (notification)
    const [message, setMessage] = useState({ text: '', type: '' });
    // State cho trạng thái loading
    const [loading, setLoading] = useState(false);

    // --- ĐÃ XÓA: Cài đặt Header global cho Axios ---
    // Header sẽ được thêm thủ công vào từng request cần xác thực.

    // Lưu user data vào localStorage mỗi khi state 'user' thay đổi
    useEffect(() => {
        if (user) {
            localStorage.setItem('user_data', JSON.stringify(user));
        } else {
            // Nếu user là null (ví dụ sau khi logout), xóa khỏi storage
            localStorage.removeItem('user_data');
        }
    }, [user]);

    // Hàm hiển thị thông báo
    const notify = useCallback((text, type = 'success') => {
        setMessage({ text, type });
        setTimeout(() => setMessage({ text: '', type: '' }), 4000);
    }, []); // setMessage là stable, không cần đưa vào dependency

    // Hàm xử lý đăng nhập
    const login = useCallback((userData, jwtToken) => {
        console.log('Login với:', { userData, jwtToken });
        localStorage.setItem('jwt_token', jwtToken);
        localStorage.setItem('user_data', JSON.stringify(userData));
        setToken(jwtToken);
        setUser(userData);
        // --- ĐÃ XÓA: Cài đặt Header global cho Axios ---
        // Chuyển hướng sau khi đăng nhập (ví dụ: về trang sách nếu là user, về admin dashboard nếu là admin)
        setRoute(userData?.role === 'admin' ? 'dashboard' : 'books'); // Thêm kiểm tra userData tồn tại
    }, []); // setToken, setUser, setRoute là stable

    // Hàm xử lý đăng xuất
    const logout = useCallback(() => {
        console.log("Logging out..."); // Thêm log để debug
        localStorage.removeItem('jwt_token');
        localStorage.removeItem('user_data');
        // --- ĐÃ XÓA: Cài đặt Header global cho Axios ---
        setToken(null);
        setUser(null);
        setRoute('home'); // Chuyển về trang chủ sau khi logout
    }, []); // setToken, setUser, setRoute là stable

    // Hàm lấy thông tin profile user
    const fetchProfile = useCallback(async (authToken) => { // Nhận token làm đối số
        if (!authToken) {
            console.log("fetchProfile: Không có token.");
            return; // Không làm gì nếu không có token
        }
        setLoading(true);
        try {
            // Thêm header Authorization thủ công
            const res = await axios.get('/profile', {
                headers: { 'Authorization': `Bearer ${authToken}` }
            });
            // Giả sử backend trả về trực tiếp data user
            const newUserData = res.data;

            // Dùng functional update để cập nhật state an toàn
            setUser(prevUser => ({
                ...(prevUser || {}), // Đảm bảo prevUser không null
                ...newUserData, // Ghi đè bằng dữ liệu mới từ API
            }));
             // Để useEffect tự động lưu vào localStorage khi 'user' thay đổi
        } catch (error) {
            console.error('Lỗi fetch profile:', error.response?.data || error.message);
            // Tự động logout nếu token không hợp lệ (401) hoặc lỗi xác thực (422)
            if (error.response && (error.response.status === 401 || error.response.status === 422)) {
                notify('Phiên làm việc không hợp lệ hoặc đã hết hạn. Vui lòng đăng nhập lại.', 'error');
                // Quan trọng: Gọi logout để xóa token lỗi
                logout();
            } else {
                notify('Không thể tải dữ liệu hồ sơ.', 'error');
            }
        } finally {
            setLoading(false);
        }
    // Chỉ phụ thuộc vào các hàm stable (logout, notify)
    }, [logout, notify]); // Bỏ setLoading vì nó stable

    // Các biến tiện ích để kiểm tra vai trò user
    const isAdmin = useMemo(() => user?.role === 'admin', [user]);
    const isLibrarian = useMemo(() => user?.role === 'librarian', [user]);
    const isLibrarianOrAdmin = useMemo(() => isAdmin || isLibrarian, [isAdmin, isLibrarian]);

    // Trả về các state và hàm cần thiết cho các component con
    return {
        user,
        token,
        route,
        setRoute,
        login,
        logout,
        message,
        loading,
        setLoading,
        notify,
        fetchProfile,
        isAdmin,
        isLibrarianOrAdmin
    };
};

