// Component Form chung cho Login/Register
const AuthForm = ({ title, route, state, setState, appState }) => {
  const isLogin = route === 'login';
  // Chỉ lấy các hàm/state cần thiết từ appState
  const { login, notify, setLoading, loading, setRoute } = appState;
  const [error, setError] = useState(''); // State lỗi cục bộ cho form

  const onSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Bắt đầu loading
    setError('');     // Xóa lỗi cũ

    try {
      // Chuẩn bị payload dựa trên route (login hay register)
      const payload = isLogin ? {
        email: state.identifier, // Login dùng email
        password: state.password
      } : {
        username: state.username,
        email: state.email,
        password: state.password,
        role: 'user' // Vai trò mặc định khi đăng ký
      };
      // Gọi API tương ứng
      const response = await axios.post(isLogin ? '/login' : '/register', payload);

      console.log('Phản hồi server:', response.data);

      if (isLogin) {
        // Xử lý login thành công
        if (response.data.token && response.data.user) {
          // Gọi hàm login từ appState để cập nhật state global và localStorage
          login(response.data.user, response.data.token);
          notify('Đăng nhập thành công!', 'success');
          // Không cần setRoute ở đây, hàm login đã xử lý
        } else {
          // Ném lỗi nếu response thiếu token hoặc user data
          throw new Error('Phản hồi không hợp lệ từ server khi đăng nhập');
        }
      } else {
        // Xử lý register thành công
        notify('Đăng ký thành công! Vui lòng đăng nhập.', 'success');
        setRoute('login'); // Chuyển hướng sang trang login sau khi đăng ký
      }
    } catch (err) { // Đổi tên biến lỗi thành err
      console.error('Lỗi xác thực:', err.response?.data || err.message);
      // Lấy thông báo lỗi từ server hoặc lỗi mặc định
      const message = err.response?.data?.msg || err.message || 'Xác thực thất bại.';
      setError(message); // Cập nhật state lỗi cục bộ
      notify(message, 'error'); // Hiển thị thông báo lỗi global
    } finally {
      setLoading(false); // Kết thúc loading dù thành công hay thất bại
    }
  };

  return (
    <Card title={title} className="max-w-md mx-auto my-12">
      <form onSubmit={onSubmit}>
        {/* Hiển thị lỗi nếu có */}
        {error && (
          <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded">
            {error}
          </div>
        )}
        {/* Chỉ hiển thị input Username cho form Register */}
        {!isLogin && (
          <InputField
            label="Username"
            id="username"
            name="username" // Thêm name attribute
            value={state.username}
            onChange={(e) => setState({ ...state, username: e.target.value })}
            required
          />
        )}
        {/* Input Email (luôn là email) */}
        <InputField
          label={"Email"}
          id={isLogin ? "identifier" : "email"} // ID khác nhau cho login/register
          name={isLogin ? "identifier" : "email"} // Thêm name attribute
          type="email" // Luôn là type email
          value={isLogin ? state.identifier : state.email}
          onChange={(e) => setState({ ...state, [isLogin ? 'identifier' : 'email']: e.target.value })}
          required
        />
        {/* Input Password */}
        <InputField
          label="Password"
          id="password"
          name="password" // Thêm name attribute
          type="password"
          value={state.password}
          onChange={(e) => setState({ ...state, password: e.target.value })}
          required
        />
        {/* Nút Submit */}
        <button
          type="submit"
          disabled={loading} // Disable khi đang loading
          // Đổi màu nút sang hồng
          className="w-full bg-rose-600 text-white p-3 rounded-lg font-semibold hover:bg-rose-700 transition duration-200 shadow-md disabled:bg-rose-400"
        >
          {loading ? 'Đang xử lý...' : title}
        </button>
      </form>
      {/* Link chuyển đổi giữa Login/Register */}
      <p className="mt-4 text-center text-sm text-stone-500">
        {isLogin ? "Chưa có tài khoản? " : "Đã có tài khoản? "}
        <button onClick={() => setRoute(isLogin ? 'register' : 'login')} className="text-rose-600 hover:text-rose-800 font-medium">
          {isLogin ? 'Đăng ký tại đây' : 'Đăng nhập tại đây'}
        </button>
      </p>
    </Card>
  );
};

