// --- Component Trang Profile ---

const ProfilePage = ({ appState }) => {
    // Lấy token trực tiếp để dùng trong các API call của component này
    const { user, notify, fetchProfile, isAdmin, isLibrarianOrAdmin, token, logout } = appState; // Thêm logout
    const [file, setFile] = useState(null); // State cho file avatar đã chọn
    const [isUploading, setIsUploading] = useState(false); // State loading cho việc upload

    // Tự động fetch profile khi component được mount *hoặc* khi token thay đổi
    useEffect(() => {
        if (token) {
            fetchProfile(token); // Truyền token vào hàm fetchProfile
        } else {
             console.log("ProfilePage: No token, cannot fetch profile.");
             // Cân nhắc logout nếu không có token mà user vẫn vào được đây (dù khó xảy ra với routing hiện tại)
             // logout();
        }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [token]); // Chỉ chạy lại khi token thay đổi

    // Xử lý khi chọn file avatar
    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    // Xử lý khi nhấn nút "Save Avatar"
    const handleUpload = async () => {
        // Kiểm tra xem đã chọn file và đã đăng nhập chưa
        if (!file || !token) {
            notify('Vui lòng chọn ảnh và đảm bảo bạn đã đăng nhập.', 'error');
            return;
        }

        setIsUploading(true); // Bắt đầu loading upload
        const formData = new FormData(); // Dùng FormData để gửi file
        formData.append('avatar', file);

        try {
            // Gọi API upload avatar, **thêm header Authorization thủ công**
            await axios.post('/profile/avatar', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data', // Quan trọng khi gửi file
                    'Authorization': `Bearer ${token}`      // Gửi token
                },
            });
            notify('Cập nhật avatar thành công!', 'success');
            setFile(null); // Xóa file đã chọn khỏi input
            fetchProfile(token); // Tải lại dữ liệu profile sau khi upload thành công
        } catch (error) {
            console.error("Lỗi upload avatar:", error.response?.data || error.message);
            const message = error.response?.data?.msg || 'Không thể tải lên avatar.';
            notify(message, 'error');
            // Xử lý lỗi token
            if (error.response && (error.response.status === 401 || error.response.status === 422)) {
                logout();
            }
        } finally {
            setIsUploading(false); // Kết thúc loading upload
        }
    };

    // Xử lý khi nhấn nút trả sách
    const handleReturn = async (borrowingId, title) => {
        // Kiểm tra đã đăng nhập chưa
        if (!token) {
             notify('Yêu cầu xác thực.', 'error');
             return;
        }
        // Xác nhận lại hành động
        if (!window.confirm(`Bạn chắc chắn muốn trả sách: "${title}"?`)) return;

        try {
            // Gọi API trả sách, **thêm header Authorization thủ công**
            await axios.post(`/return/${borrowingId}`, {}, { // Gửi object rỗng làm data
                 headers: { 'Authorization': `Bearer ${token}` }
            });
            notify(`Đã trả sách '${title}' thành công!`, 'success');
            fetchProfile(token); // Tải lại lịch sử mượn sau khi trả sách
        } catch (error) {
            console.error("Lỗi trả sách:", error.response?.data || error.message);
            notify(error.response?.data?.msg || 'Không thể trả sách.', 'error');
             // Xử lý lỗi token
            if (error.response && (error.response.status === 401 || error.response.status === 422)) {
                logout();
            }
        }
        // Không cần setLoading ở đây vì không có state loading cục bộ
    };

    // Nếu chưa có user data (đang load hoặc chưa login), hiển thị thông báo
    // Quan trọng: Kiểm tra `appState.loading` thay vì state loading cục bộ
    if (appState.loading && !user) return <p className="text-center text-xl mt-12 text-stone-600">Đang tải hồ sơ...</p>;
    if (!user) return <p className="text-center text-xl mt-12 text-stone-600">Vui lòng đăng nhập để xem hồ sơ.</p>;


    // Giao diện trang Profile
    return (
        <div className="container mx-auto px-4 py-8 grid lg:grid-cols-3 gap-8">
            {/* Card thông tin User */}
            <Card className="lg:col-span-1 h-fit">
                <div className="flex flex-col items-center">
                    {/* Avatar */}
                    <div className="relative w-32 h-32 rounded-full overflow-hidden mb-4 border-4 border-rose-500 shadow-lg">
                        <img
                            // Dùng avatar_url từ user data, fallback về ảnh placeholder màu hồng
                            src={user.avatar_url || 'https://placehold.co/128x128/E879F9/FFFFFF?text=P'}
                            alt="User Avatar"
                            className="object-cover w-full h-full"
                            // Xử lý lỗi cơ bản nếu ảnh không load được
                            onError={(e) => { e.target.onerror = null; e.target.src="https://placehold.co/128x128/E879F9/FFFFFF?text=Err" }}
                        />
                    </div>
                    {/* Username và Role */}
                    <h3 className="text-3xl font-bold text-stone-900">{user.username}</h3>
                    <p className={`text-sm font-semibold mt-1 py-1 px-3 rounded-full ${isAdmin ? 'bg-red-100 text-red-600' : isLibrarianOrAdmin ? 'bg-rose-100 text-rose-600' : 'bg-gray-100 text-stone-600'}`}>
                        {/* Thêm fallback nếu role không có */}
                        {user.role?.toUpperCase() || 'USER'}
                    </p>
                    {/* Thông tin khác */}
                    <div className="mt-6 w-full space-y-2 text-stone-600">
                        <p className="flex justify-between items-center border-b pb-1">Email: <span className="font-medium text-stone-800 break-all">{user.email}</span></p>
                        <p className="flex justify-between items-center">Ngày tham gia: <span className="font-medium text-stone-800">{user.created_at ? new Date(user.created_at).toLocaleDateString() : 'N/A'}</span></p>
                        <p className="text-xs break-words pt-4 text-center text-stone-400">User ID: {user.id}</p>
                    </div>
                </div>
            </Card>

            {/* Phần Upload Avatar và Lịch sử mượn */}
            <div className="lg:col-span-2 space-y-8">
                {/* Card Upload Avatar */}
                <Card title="Đổi Avatar">
                    <div className="flex flex-col sm:flex-row space-y-4 sm:space-y-0 sm:space-x-4 items-center">
                        <input type="file" onChange={handleFileChange} accept="image/*" className="block w-full text-sm text-stone-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-rose-50 file:text-rose-700 hover:file:bg-rose-100" />
                        <button
                            onClick={handleUpload}
                            // Disable nút nếu chưa chọn file, đang upload, hoặc chưa đăng nhập
                            disabled={!file || isUploading || !token}
                            className="w-full sm:w-auto flex-shrink-0 bg-emerald-600 text-white p-3 rounded-lg font-semibold hover:bg-emerald-700 transition duration-200 shadow-md disabled:bg-emerald-400"
                        >
                            {isUploading ? 'Đang tải lên...' : 'Lưu Avatar'}
                        </button>
                    </div>
                    <p className="mt-2 text-sm text-stone-500">Kích thước tối đa: 2MB. Định dạng: PNG, JPG, JPEG, GIF.</p>
                </Card>

                {/* Card Lịch sử mượn */}
                <Card title="Lịch sử mượn sách">
                    {/* Kiểm tra borrowing_history có tồn tại và có phần tử không */}
                    {!user.borrowing_history || user.borrowing_history.length === 0 ? (
                        <p className="text-stone-500">Bạn chưa mượn cuốn sách nào.</p>
                    ) : (
                        <div className="space-y-4 max-h-96 overflow-y-auto pr-2">
                            {/* Map qua lịch sử mượn */}
                            {user.borrowing_history.map((item) => (
                                <div key={item.id} className="p-4 border border-gray-100 rounded-xl shadow-sm hover:shadow-md transition duration-200 flex justify-between items-center bg-gray-50">
                                    {/* Thông tin sách */}
                                    <div>
                                        <p className="font-semibold text-stone-900">{item.book_title}</p>
                                        <p className="text-sm text-stone-600">Mượn ngày: {new Date(item.borrow_date).toLocaleDateString()}</p>
                                        <p className="text-sm text-red-500 font-medium">Hạn trả: {new Date(item.due_date).toLocaleDateString()}</p>
                                    </div>
                                    {/* Trạng thái và nút trả sách */}
                                    <div className="flex items-center space-x-3">
                                        <span className={`px-3 py-1 text-xs font-semibold rounded-full ${
                                            item.status === 'returned' ? 'bg-emerald-100 text-emerald-600' :
                                            // Kiểm tra cả ngày hết hạn và status để xác định quá hạn
                                            (new Date(item.due_date) < new Date() && item.status !== 'returned' ? 'bg-red-100 text-red-600' : 'bg-yellow-100 text-yellow-600')
                                        }`}>
                                            {/* Thêm fallback nếu status không có */}
                                            {item.status?.toUpperCase() || 'UNKNOWN'}
                                        </span>
                                        {/* Chỉ hiển thị nút trả sách nếu status là 'borrowed' */}
                                        {item.status === 'borrowed' && (
                                            <button
                                                onClick={() => handleReturn(item.id, item.book_title)}
                                                className="p-2 bg-rose-500 text-white rounded-full hover:bg-rose-600 transition shadow-md"
                                                title="Trả sách"
                                            >
                                                <RotateCcw className="w-5 h-5" />
                                            </button>
                                        )}
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </Card>
            </div>
        </div>
    );
};


// --- Component BookModal (ĐÃ DI CHUYỂN RA NGOÀI BookManager) ---
const BookModal = ({ appState, currentBook, form, handleInputChange, handleSubmit, handleCloseModal }) => {
    // Lấy trạng thái loading trực tiếp từ appState
    const { loading } = appState;
    return (
        // Overlay mờ bao phủ toàn màn hình
        <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex justify-center items-center z-50 p-4 overflow-y-auto">
            {/* Card chứa form, thêm margin top/bottom để tránh bị che */}
            <Card title={currentBook ? "Sửa thông tin sách" : "Thêm sách mới"} className="w-full max-w-lg my-8">
                <form onSubmit={handleSubmit}>
                    <InputField label="Tiêu đề" id="title" name="title" value={form.title || ''} onChange={handleInputChange} required />
                    <InputField label="Tác giả" id="author" name="author" value={form.author || ''} onChange={handleInputChange} required />
                    <InputField label="ISBN" id="isbn" name="isbn" value={form.isbn || ''} onChange={handleInputChange} />
                    <div className="grid grid-cols-2 gap-4">
                        <InputField label="Năm xuất bản" id="published_year" name="published_year" type="number" value={form.published_year || ''} onChange={handleInputChange} />
                        <InputField label="Thể loại" id="genre" name="genre" value={form.genre || ''} onChange={handleInputChange} />
                    </div>
                    {/* Thêm min="0" để số lượng không âm */}
                    <InputField label="Số lượng trong kho" id="stock" name="stock" type="number" value={form.stock || ''} onChange={handleInputChange} required min="0" />

                    {/* Nút Cancel và Save */}
                    <div className="flex justify-end space-x-4 mt-6">
                        <button type="button" onClick={handleCloseModal} className="px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-100 transition">Hủy</button>
                        <button type="submit" disabled={loading} className="flex items-center space-x-2 px-4 py-2 bg-rose-600 text-white rounded-lg hover:bg-rose-700 transition disabled:bg-rose-400">
                            <Save className="w-5 h-5" />
                            <span>{loading ? 'Đang lưu...' : 'Lưu sách'}</span>
                        </button>
                    </div>
                </form>
            </Card>
        </div>
    );
};


// --- Component Quản lý Sách (Chỉ Admin/Librarian) ---
const BookManager = ({ appState, books, fetchBooks }) => {
  // Lấy token ở đây để dùng cho các API call trong BookManager
  const { isLibrarianOrAdmin, isAdmin, notify, setLoading, loading, token, logout } = appState; // Thêm logout
  const [isModalOpen, setIsModalOpen] = useState(false); // State đóng/mở modal
  const [currentBook, setCurrentBook] = useState(null); // State lưu sách đang được sửa (null nếu là thêm mới)
  // State cho form được giữ cục bộ trong BookManager
  const [form, setForm] = useState({ title: '', author: '', isbn: '', published_year: '', stock: 1, genre: '' });

  // Cập nhật state 'form' cục bộ khi 'currentBook' thay đổi
  useEffect(() => {
    if (currentBook) {
      // Nếu sửa sách, điền thông tin sách vào form
      setForm({
          title: currentBook.title || '',
          author: currentBook.author || '',
          isbn: currentBook.isbn || '',
          published_year: currentBook.published_year || '',
          stock: currentBook.stock || 0,
          genre: currentBook.genre || ''
      });
    } else {
      // Nếu thêm mới, reset form về giá trị mặc định
      setForm({ title: '', author: '', isbn: '', published_year: '', stock: 1, genre: '' });
    }
  }, [currentBook]);

  // Nếu user không phải Librarian hoặc Admin, hiển thị thông báo lỗi
  if (!isLibrarianOrAdmin) {
    return <p className="text-center text-red-500 text-xl mt-12">Yêu cầu quyền truy cập: Chỉ Thủ thư và Admin mới có thể truy cập Quản lý sách.</p>;
  }

  // Hàm mở modal (có thể truyền sách vào để sửa, hoặc không truyền để thêm mới)
  const handleOpenModal = (book = null) => {
    setCurrentBook(book); // Cập nhật sách đang sửa/thêm
    setIsModalOpen(true); // Mở modal
  };

  // Hàm đóng modal
  const handleCloseModal = () => {
    setIsModalOpen(false); // Đóng modal
    setCurrentBook(null); // Reset sách đang sửa/thêm
  };

  // Hàm xử lý khi input trong form thay đổi
  const handleInputChange = (e) => {
    const { name, value, type } = e.target;
    // Cập nhật state 'form' cục bộ
    setForm(prev => ({
      ...prev,
      // Xử lý riêng cho input dạng số
      [name]: type === 'number' ? (parseInt(value) || 0) : value
    }));
  };

  // Hàm xử lý khi submit form (Thêm/Sửa sách)
  const handleSubmit = async (e) => {
    e.preventDefault(); // Ngăn form reload trang
    // Kiểm tra token
    if (!token) {
        notify('Yêu cầu xác thực.', 'error');
        return;
    }
    setLoading(true); // Bắt đầu loading

    try {
        // Chuẩn bị header Authorization
        const config = { headers: { 'Authorization': `Bearer ${token}` } };
        if (currentBook) {
            // Nếu đang sửa sách (currentBook không null), gọi API PUT
            await axios.put(`/books/${currentBook.id}`, form, config);
            notify('Cập nhật sách thành công!', 'success');
        } else {
            // Nếu đang thêm mới (currentBook là null), gọi API POST
            await axios.post('/books', form, config);
            notify('Thêm sách thành công!', 'success');
        }
        fetchBooks(token); // Tải lại danh sách sách sau khi lưu, **truyền token vào**
        handleCloseModal(); // Đóng modal sau khi thành công
    } catch (error) {
        console.error("Lỗi lưu sách:", error.response?.data || error.message);
        notify(error.response?.data?.msg || 'Lỗi lưu dữ liệu sách.', 'error');
         // Xử lý lỗi token
        if (error.response && (error.response.status === 401 || error.response.status === 422)) {
            logout();
        }
    } finally {
        setLoading(false); // Kết thúc loading
    }
  };

  // Hàm xử lý khi xóa sách
  const handleDelete = async (bookId, title) => {
    // Chỉ Admin mới được xóa
    if (!isAdmin) {
      notify('Chỉ Admin mới có thể xóa sách.', 'error');
      return;
    }
    // Kiểm tra token
    if (!token) {
        notify('Yêu cầu xác thực.', 'error');
        return;
    }
    // Xác nhận lại hành động
    if (!window.confirm(`Bạn chắc chắn muốn xóa sách: "${title}"?`)) return;

    try {
        setLoading(true); // Bắt đầu loading
        // Gọi API DELETE, **thêm header Authorization thủ công**
        await axios.delete(`/books/${bookId}`, {
            headers: { 'Authorization': `Bearer ${token}` }
        });
        notify('Xóa sách thành công!', 'success');
        fetchBooks(token); // Tải lại danh sách sách sau khi xóa, **truyền token vào**
    } catch (error) {
        console.error("Lỗi xóa sách:", error.response?.data || error.message);
        notify(error.response?.data?.msg || 'Lỗi xóa sách.', 'error');
         // Xử lý lỗi token
        if (error.response && (error.response.status === 401 || error.response.status === 422)) {
            logout();
        }
    } finally {
        setLoading(false); // Kết thúc loading
    }
  };

  // Giao diện BookManager
  return (
    <>
      {/* Render BookModal nếu isModalOpen là true, truyền các props cần thiết */}
      {isModalOpen && (
          <BookModal
              appState={appState} // Truyền toàn bộ appState để modal lấy loading
              currentBook={currentBook}
              form={form} // Truyền state form cục bộ
              handleInputChange={handleInputChange} // Truyền hàm xử lý input change
              handleSubmit={handleSubmit}       // Truyền hàm xử lý submit
              handleCloseModal={handleCloseModal}     // Truyền hàm đóng modal
          />
      )}
      {/* Tiêu đề và nút Add New Book */}
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-3xl font-bold text-stone-800">Quản lý sách</h2>
        <button
          onClick={() => handleOpenModal()} // Mở modal để thêm mới (không truyền sách)
          className="flex items-center space-x-2 px-4 py-2 bg-rose-600 text-white rounded-lg font-semibold hover:bg-rose-700 transition shadow-md"
        >
          <Plus className="w-5 h-5" />
          <span>Thêm sách mới</span>
        </button>
      </div>

      {/* Bảng hiển thị danh sách sách */}
       <div className="overflow-x-auto bg-white rounded-xl shadow-2xl">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-stone-500 uppercase tracking-wider">Tiêu đề</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-stone-500 uppercase tracking-wider">Tác giả</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-stone-500 uppercase tracking-wider">ISBN</th>
              <th className="px-6 py-3 text-center text-xs font-medium text-stone-500 uppercase tracking-wider">Trong kho</th>
              <th className="px-6 py-3 text-center text-xs font-medium text-stone-500 uppercase tracking-wider">Có sẵn</th>
              <th className="px-6 py-3 text-center text-xs font-medium text-stone-500 uppercase tracking-wider">Hành động</th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
             {/* Đảm bảo 'books' là một mảng trước khi map */}
            {Array.isArray(books) && books.map((book) => (
              <tr key={book.id} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-stone-900">{book.title}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-stone-500">{book.author}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-stone-500">{book.isbn || 'N/A'}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-center text-stone-900">{book.stock}</td>
                <td className={`px-6 py-4 whitespace-nowrap text-sm text-center font-bold ${book.available_copies === 0 ? 'text-red-500' : 'text-emerald-600'}`}>
                  {book.available_copies}
                </td>
                {/* Nút Sửa và Xóa */}
                <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium flex justify-center space-x-3">
                  {/* Nút Sửa: Mở modal và truyền sách hiện tại vào */}
                  <button onClick={() => handleOpenModal(book)} className="text-rose-600 hover:text-rose-900 p-1 rounded-full hover:bg-rose-50 transition" title="Sửa sách"><Settings className="w-5 h-5" /></button>
                  {/* Nút Xóa: Chỉ enable cho Admin và không bị disable khi đang loading */}
                  <button onClick={() => handleDelete(book.id, book.title)} disabled={!isAdmin || loading} className={`p-1 rounded-full transition ${isAdmin ? 'text-red-600 hover:text-red-900 hover:bg-red-50' : 'text-stone-400 cursor-not-allowed'} ${loading ? 'opacity-50' : ''}`} title="Xóa sách (Chỉ Admin)"><Trash2 className="w-5 h-5" /></button>
                </td>
              </tr>
            ))}
             {/* Hiển thị thông báo nếu không có sách */}
             {(!Array.isArray(books) || books.length === 0) && !loading && (
                <tr>
                    <td colSpan="6" className="px-6 py-4 text-center text-stone-500">Không có sách nào trong bảng quản lý.</td>
                </tr>
             )}
          </tbody>
        </table>
      </div>
    </>
  );
};


// --- Component Trang Dashboard (Hiển thị danh sách sách cho mọi user) ---
const DashboardPage = ({ appState }) => {
  // Lấy token và logout ở đây để truyền xuống các hàm con
  const { isLibrarianOrAdmin, notify, loading, setLoading, token, logout } = appState;
  const [books, setBooks] = useState([]); // State danh sách sách cục bộ
  const [error, setError] = useState(null); // State lỗi cục bộ

  // Hàm fetchBooks giờ nhận token làm đối số và chứa logic logout
  const fetchBooks = useCallback(async (authToken) => {
    // Nếu không có token, báo lỗi và không fetch
    if (!authToken) {
        console.log("fetchBooks: Không có token.");
        setError("Yêu cầu xác thực để xem sách.");
        // Không cần notify ở đây nữa vì đã có error message
        setBooks([]); // Xóa danh sách sách cũ
        setLoading(false); // Đảm bảo tắt loading
        return;
    }
    setLoading(true); // Bắt đầu loading
    setError(null);   // Xóa lỗi cũ
    try {
      console.log('Đang fetch sách với token...');
      // Gọi API lấy sách, **thêm header Authorization thủ công**
      const res = await axios.get('/books', {
          headers: { 'Authorization': `Bearer ${authToken}` }
      });
      console.log('Phản hồi books:', res.data);
      // Cập nhật state sách, đảm bảo là mảng
      setBooks(Array.isArray(res.data) ? res.data : []);
    } catch (error) {
      console.error('Lỗi fetch sách:', error.response?.data || error.message);
      const errorMessage = error.response?.data?.msg || 'Không thể tải danh sách sách.';
      setError(errorMessage);
      notify(errorMessage, 'error');
      // Xử lý lỗi xác thực (token hết hạn/không hợp lệ) -> Tự động logout
      if (error.response && (error.response.status === 401 || error.response.status === 422)) {
          logout(); // Gọi hàm logout từ appState
      }
    } finally {
      setLoading(false); // Kết thúc loading
    }
  // Phụ thuộc vào các hàm stable (setLoading, notify, logout)
  }, [setLoading, notify, logout]);

  // useEffect để fetch sách khi component mount *hoặc* khi token thay đổi
  useEffect(() => {
    console.log('DashboardPage useEffect: Token =', token);
    // Gọi fetchBooks chỉ khi có token
    if (token) {
        fetchBooks(token);
    } else {
        // Nếu không có token lúc mount, set lỗi và xóa sách
        setBooks([]);
        setError("Vui lòng đăng nhập để xem sách.");
        setLoading(false);
    }
  // Chỉ chạy lại khi token thay đổi
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [token]);

  // Hàm xử lý khi mượn sách
  const handleBorrow = async (bookId, title) => {
    // Kiểm tra token
    if (!token) {
        notify('Vui lòng đăng nhập để mượn sách.', 'error');
        return;
    }
    // Xác nhận hành động
    if (!window.confirm(`Xác nhận mượn sách: "${title}"?`)) return;

    // Tạm thời tắt loading global để tránh che nút bấm
    // setLoading(true); // Tạm thời comment setLoading ở đây
    try {
        // Gọi API mượn sách, **thêm header Authorization thủ công**
        const res = await axios.post(`/borrow/${bookId}`, {}, { // Gửi object rỗng làm body
             headers: { 'Authorization': `Bearer ${token}` }
        });
        notify(res.data.msg, 'success'); // Hiển thị thông báo từ server
        fetchBooks(token); // Tải lại danh sách sách, **truyền token vào**
    } catch (error) {
        console.error("Lỗi mượn sách:", error.response?.data || error.message);
        notify(error.response?.data?.msg || 'Không thể mượn sách.', 'error');
        // Xử lý lỗi token
        if (error.response && (error.response.status === 401 || error.response.status === 422)) {
            logout();
        }
    } finally {
        // setLoading(false); // Tắt loading global

        // Cần re-render component để cập nhật disabled state của nút nếu dùng loading cục bộ
    }
  };

    // --- Render Giao diện ---

    // Hiển thị trạng thái Loading ban đầu
    if (loading && books.length === 0 && !error) {
      return <p className="text-center text-xl mt-12 text-rose-600">Đang tải sách...</p>;
    }

    // Hiển thị trạng thái lỗi (khi chưa đăng nhập hoặc token đã bị xóa)
    if (error && !token) {
        return (
            <div className="text-center py-8">
                <p className="text-xl text-red-600">{error || "Vui lòng đăng nhập để xem sách."}</p>
                 <button
                    onClick={() => appState.setRoute('login')} // Lấy setRoute từ appState props
                    className="mt-4 px-4 py-2 bg-rose-600 text-white rounded hover:bg-rose-700"
                >
                    Đến trang đăng nhập
                </button>
            </div>
        );
    }
    // Hiển thị trạng thái lỗi (khi đã đăng nhập nhưng fetch lỗi)
    else if (error && token) { // Chỉ hiển thị nút retry nếu vẫn còn token
         return (
            <div className="text-center py-8">
                <p className="text-xl text-red-600">{error}</p>
                <button
                    onClick={() => fetchBooks(token)} // Thử lại với token hiện tại
                    className="mt-4 px-4 py-2 bg-rose-600 text-white rounded hover:bg-rose-700"
                >
                    Thử tải lại sách
                </button>
            </div>
        );
    }

  // Hiển thị nội dung chính
  return (
    <div className="container mx-auto px-4 py-8">
      {/* Hiển thị component BookManager nếu user là Librarian hoặc Admin */}
      {isLibrarianOrAdmin && <BookManager appState={appState} books={books} fetchBooks={fetchBooks} />}

      {/* Tiêu đề Catalog */}
      <h2 className={`text-3xl font-bold text-stone-800 ${isLibrarianOrAdmin ? 'mt-12 border-t pt-8' : 'mt-8'}`}>
        Danh mục sách
      </h2>

      {/* Danh sách sách dạng grid */}
      {/* Đảm bảo books là mảng và không rỗng */}
      {!Array.isArray(books) || books.length === 0 ? (
        // Chỉ hiển thị "Không có sách" nếu không loading và không có lỗi
        !loading && !error && <p className="text-center text-stone-600 mt-8">Không có sách nào trong danh mục.</p>
      ) : (
        <div className="mt-6 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {/* Map qua danh sách sách */}
          {books.map((book) => (
            <Card key={book.id} className="flex flex-col justify-between hover:shadow-rose-100 transition duration-300 ease-in-out">
              {/* Phần thông tin sách */}
              <div>
                <h3 className="text-xl font-semibold text-rose-700">{book.title}</h3>
                <p className="text-sm text-stone-600 italic">bởi {book.author}</p>
                <p className="text-xs mt-2 bg-stone-100 text-stone-700 px-2 py-1 rounded-md w-fit">
                  Thể loại: {book.genre || 'N/A'}
                </p>
              </div>
              {/* Phần số lượng và nút Mượn */}
              <div className="mt-4 pt-4 border-t">
                <p className="text-sm font-medium text-stone-800">
                  Có sẵn: <span className={book.available_copies > 0 ? 'text-emerald-600' : 'text-red-500'}>
                    {book.available_copies} / {book.stock}
                  </span>
                </p>
                <button
                  onClick={() => handleBorrow(book.id, book.title)}
                  // Disable nút nếu hết sách, đang loading global, hoặc chưa đăng nhập
                  disabled={book.available_copies === 0 || loading || !token}
                  className={`mt-3 w-full p-2 bg-rose-600 text-white rounded-lg font-medium hover:bg-rose-700 transition duration-150 disabled:bg-gray-400 disabled:cursor-not-allowed ${loading ? 'opacity-70' : ''}`} // Thêm opacity khi loading
                >
                  {/* Text nút thay đổi tùy trạng thái */}
                  {token ? (book.available_copies === 0 ? 'Hết sách' : 'Mượn sách') : 'Đăng nhập để mượn'}
                </button>
              </div>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
};


