// --- Component App Chính ---
const App = () => {
  // Lấy state và các hàm từ custom hook
  const appState = useAppState();
  // Destructure các giá trị cần thiết để dùng trong logic routing và truyền props
  // Lấy thêm `logout` để có thể tự động logout khi token lỗi ở routing
  const { route, token, user, isAdmin, logout } = appState;

  // Logic render nội dung chính dựa trên route và trạng thái đăng nhập
  const renderContent = useMemo(() => {
    // ---- Các Route khi CHƯA ĐĂNG NHẬP ----
    if (!token) {
        switch (route) {
            case 'login': return <LoginPage appState={appState} />;
            if (!token) {
              switch (route) {
                  case 'login': 
                      return <LoginPage appState={appState} />; 
                      break; // <-- THÊM DÒNG NÀY
                  case 'register': 
                      return <RegisterPage appState={appState} />;
                  // ... các case khác
              }
          }
            case 'register': return <RegisterPage appState={appState} />;
            // Mọi route khác khi chưa đăng nhập đều về trang chủ
            case 'home':
            // Nếu cố vào route cần login mà chưa có token -> về home
            case 'profile':
            case 'dashboard':
            case 'books':
            case 'users':
            case 'settings':
            default: return <HomePage />;
        }
    }

    // ---- Các Route khi ĐÃ ĐĂNG NHẬP ----
    // Token tồn tại ở đây
    switch (route) {
      case 'profile':
        return <ProfilePage appState={appState} />;

      // Route 'dashboard' hiển thị khác nhau tùy vai trò
      case 'dashboard':
         return isAdmin
             ? <AdminDashboard appState={appState} /> // Nếu là admin -> Admin Dashboard
             : <DashboardPage appState={appState} />; // Mặc định là trang sách cho user thường

      // Route 'books' luôn là trang danh sách/quản lý sách
      case 'books':
          return <DashboardPage appState={appState} />; // DashboardPage tự xử lý quyền Admin/Librarian bên trong

      // ---- Các Route chỉ dành cho Admin ----
      case 'users':
          // Nếu là admin -> trang quản lý user, nếu không -> về trang sách (thay vì home)
          return isAdmin ? <UserManagementPage appState={appState} /> : <DashboardPage appState={appState} />;
      case 'settings':
          // Nếu là admin -> trang settings, nếu không -> về trang sách
          return isAdmin ? <SettingsPage appState={appState} /> : <DashboardPage appState={appState} />;

      // ---- Route mặc định khi đã đăng nhập ----
      case 'home': // Cho phép quay về trang chủ ngay cả khi đã đăng nhập
          return <HomePage />;
      default:
         // Nếu route không khớp các case trên (ví dụ: gõ url linh tinh):
         // Nếu là admin -> về Admin Dashboard
         // Nếu không phải admin -> về trang sách
         console.warn(`Unknown route accessed while logged in: ${route}. Redirecting...`);
         return isAdmin
             ? <AdminDashboard appState={appState} />
             : <DashboardPage appState={appState} />;
    }
  // Dependencies: Logic render phụ thuộc vào route, token, user, isAdmin và chính appState (để truyền xuống)
  }, [route, token, user, isAdmin, appState]);

  // Giao diện App chính
  return (
    // Đổi màu nền sang xám đá sáng hơn
    <div className="min-h-screen bg-stone-100 font-sans">
      {/* Header luôn hiển thị */}
      <Header user={user} setRoute={appState.setRoute} logout={logout} />
      {/* Nội dung chính được render dựa trên logic trong renderContent */}
      <main className="pb-16">
        {renderContent}
      </main>
      {/* Thông báo luôn hiển thị (nếu có message) */}
      <Notification message={appState.message} />
    </div>
  );
};


// --- Component Admin Dashboard ---
const AdminDashboard = ({ appState }) => {
  // Kiểm tra quyền admin trước khi render (dù logic App đã kiểm tra)
  // Nếu không phải admin, có thể render trang sách hoặc trang chủ
  if (!appState.isAdmin) return <DashboardPage appState={appState} />;

  return (
    // Thêm container và padding
    <div className="container mx-auto p-8 space-y-6">
      <h1 className="text-3xl font-bold text-stone-800">Admin Dashboard</h1>
      {/* Grid chứa các card chức năng */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Card Manage Books */}
        <DashboardCard
          title="Quản lý sách"
          // Thêm màu cho icon
          icon={<BookOpen className="h-8 w-8 text-rose-600" />}
          // onClick sẽ set route thành 'books'
          onClick={() => appState.setRoute('books')}
        />
        {/* Card Manage Users */}
        <DashboardCard
          title="Quản lý người dùng"
          icon={<User className="h-8 w-8 text-rose-600" />}
          onClick={() => appState.setRoute('users')}
        />
        {/* Card Settings */}
        <DashboardCard
          title="Cài đặt"
          icon={<Settings className="h-8 w-8 text-rose-600" />}
          onClick={() => appState.setRoute('settings')}
        />
      </div>
       {/* Có thể thêm các widget hoặc thông tin khác chỉ dành cho admin ở đây */}
    </div>
  );
};

