const DashboardCard = ({ title, icon, onClick }) => {
  return (
    <button
      onClick={onClick}
      // Thêm nhiều style hơn cho đẹp: bo góc, shadow, transition, border trái màu hồng
      className="p-6 bg-white rounded-xl shadow-lg hover:shadow-xl transition-shadow duration-200 text-left w-full border-l-4 border-rose-500 hover:border-rose-700 focus:outline-none focus:ring-2 focus:ring-rose-500 focus:ring-opacity-50"
    >
      <div className="flex items-center gap-4">
        {icon}
        {/* Đổi font thành semibold */}
