// --- Main App Component ---
const App = () => {
  const appState = useAppState();

  const renderContent = useMemo(() => {
    switch (appState.route) {
      case 'login':
        return <LoginPage appState={appState} />;
      case 'register':
        return <RegisterPage appState={appState} />;
      case 'profile':
        return appState.token ? <ProfilePage appState={appState} /> : <HomePage />;

      // SỬA: Thêm route 'books'
      case 'books':
        return <DashboardPage appState={appState} />;

      // SỬA: Đổi tên 'dashboard' thành 'admin_dashboard'
      case 'admin_dashboard':
        return appState.user?.role === 'admin' ? (
          <AdminDashboard appState={appState} />
        ) : (
          <DashboardPage appState={appState} /> // Người dùng thường sẽ thấy sách
        );

      case 'home':
      default:
        return <HomePage />;
    }
  }, [appState.route, appState]); // Thêm appState vào dependency array

  return (
    <div className="min-h-screen bg-gray-50 font-sans">
      <Header user={appState.user} setRoute={appState.setRoute} logout={appState.logout} />
      <main className="pb-16">
        {renderContent}
      </main>
      <Notification message={appState.message} />
    </div>
  );
};

const AdminDashboard = ({ appState }) => {
  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Admin Dashboard</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <DashboardCard
          title="Manage Books"
          icon={<BookOpen className="h-8 w-8" />}
          // SỬA: Điều hướng đến 'books'
          onClick={() => appState.setRoute('books')} 
        />
        <DashboardCard
          title="Manage Users"
          icon={<User className="h-8 w-8" />}
          onClick={() => appState.notify('User management coming soon!', 'success')}
        />
        <DashboardCard
          title="Settings"
          icon={<Settings className="h-8 w-8" />}
          onClick={() => appState.notify('Settings coming soon!', 'success')}
        />
      </div>
    </div>
  );
};

const DashboardCard = ({ title, icon, onClick }) => {
