import React, { useState, useEffect, useCallback, useMemo } from 'react';
import axios from 'axios';
import { LogIn, User, BookOpen, Settings, Home, Plus, Trash2, RotateCcw, Save } from 'lucide-react';

// Cấu hình Axios trỏ đến backend Flask
const API_BASE_URL = 'http://127.0.0.1:5001/api'; // Đảm bảo port khớp
axios.defaults.baseURL = API_BASE_URL;
// KHÔNG SET HEADER MẶC ĐỊNH Ở ĐÂY NỮA

// --- Cài đặt Tailwind CDN (Giữ nguyên) ---
(function() {
  if (!document.getElementById('tailwind-cdn')) {
    const script = document.createElement('script');
    script.src = 'https://cdn.tailwindcss.com';
    script.id = 'tailwind-cdn';
    document.head.appendChild(script);
  }
})();

// --- Custom Hook quản lý State Global ---
const useAppState = () => {
    // State khởi tạo an toàn từ localStorage
    const [user, setUser] = useState(() => {
        try { const d = localStorage.getItem('user_data'); return d && d !== 'undefined' && d !== 'null' ? JSON.parse(d) : null; }
        catch (e) { console.error("Lỗi parse user_data:", e); localStorage.removeItem('user_data'); return null; }
    });
    const [token, setToken] = useState(() => {
      const t = localStorage.getItem('jwt_token'); return (t && t !== 'null' && t !== 'undefined') ? t : null;
    });
    const [route, setRoute] = useState(token ? (user?.role === 'admin' ? 'dashboard' : 'books') : 'home');
    const [message, setMessage] = useState({ text: '', type: '' });
    const [loading, setLoading] = useState(false);

    // Lưu user vào localStorage khi thay đổi
    useEffect(() => {
        if (user) { localStorage.setItem('user_data', JSON.stringify(user)); }
        else { localStorage.removeItem('user_data'); }
    }, [user]);

    // Hàm thông báo (stable)
    const notify = useCallback((text, type = 'success') => {
        setMessage({ text, type }); setTimeout(() => setMessage({ text: '', type: '' }), 4000);
    }, []);

    // Hàm logout (stable)
    const logout = useCallback(() => {
        console.log("Đang đăng xuất...");
        localStorage.removeItem('jwt_token'); localStorage.removeItem('user_data');
        setToken(null); setUser(null); setRoute('home');
    }, []); // Các setter là stable

    // Hàm login (stable)
    const login = useCallback((userData, jwtToken) => {
        console.log('Login với:', { userData, jwtToken });
        localStorage.setItem('jwt_token', jwtToken); localStorage.setItem('user_data', JSON.stringify(userData));
        setToken(jwtToken); setUser(userData);
        setRoute(userData?.role === 'admin' ? 'dashboard' : 'books');
    }, [setRoute]); // setRoute stable

    // Hàm fetch profile (stable) - NHẬN TOKEN LÀM ĐỐI SỐ
    const fetchProfile = useCallback(async (authToken) => {
        if (!authToken) { console.log("fetchProfile: Không có token."); return; }
        // Không setLoading global ở đây
        try {
            // GỬI TOKEN TRONG HEADER
            const res = await axios.get('/profile', { headers: { 'Authorization': `Bearer ${authToken}` } });
            setUser(prevUser => ({ ...(prevUser || {}), ...res.data }));
        } catch (error) {
            console.error('Lỗi fetch profile:', error.response?.data || error.message);
            if (error.response && (error.response.status === 401 || error.response.status === 422)) {
                notify('Phiên làm việc lỗi/hết hạn.', 'error'); logout(); // Tự động logout
            } else { notify('Không thể tải hồ sơ.', 'error'); }
        }
    }, [logout, notify]); // Phụ thuộc hàm stable

    const isAdmin = useMemo(() => user?.role === 'admin', [user]);
    const isLibrarian = useMemo(() => user?.role === 'librarian', [user]);
    const isLibrarianOrAdmin = useMemo(() => isAdmin || isLibrarian, [isAdmin, isLibrarian]);

    return { user, token, route, setRoute, login, logout, message, loading, setLoading, notify, fetchProfile, isAdmin, isLibrarianOrAdmin };
};

// --- Các Component Chung ---

const Notification = ({ message }) => { /* Giữ nguyên */ if (!message || !message.text) return null; const c = message.type === 'success' ? 'bg-emerald-500' : 'bg-red-500'; return <div className={`fixed top-4 right-4 z-50 p-4 rounded-lg text-white font-semibold shadow-xl ${c}`}>{message.text}</div>; };
const Header = ({ user, setRoute, logout }) => ( /* Giữ nguyên */ <header className="bg-stone-900 shadow-xl text-white sticky top-0 z-40"><div className="container mx-auto px-4 py-4 flex justify-between items-center"><div className="flex items-center space-x-4 cursor-pointer" onClick={() => setRoute('home')}><BookOpen className="h-7 w-7 text-rose-400" /><h1 className="text-2xl font-bold text-rose-100">LMS Pro</h1></div><nav className="flex items-center space-x-4"><NavItem icon={Home} label="Home" onClick={() => setRoute('home')} />{user ? (<><NavItem icon={BookOpen} label="Books" onClick={() => setRoute('books')} /><NavItem icon={User} label="Profile" onClick={() => setRoute('profile')} /><button onClick={logout} className="flex items-center space-x-2 px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700"><LogIn className="h-5 w-5 rotate-180" /><span>Logout ({user.username || 'User'})</span></button></>) : (<><NavItem icon={LogIn} label="Login" onClick={() => setRoute('login')} /><NavItem icon={User} label="Register" onClick={() => setRoute('register')} /></>)}</nav></div></header> );
const NavItem = ({ icon: Icon, label, onClick }) => ( /* Giữ nguyên */ <button onClick={onClick} className="flex items-center space-x-2 px-3 py-2 rounded-lg text-stone-300 hover:bg-stone-800 hover:text-white"><Icon className="h-5 w-5" /><span>{label}</span></button> );
const Card = ({ title, children, className = '' }) => ( /* Giữ nguyên */ <div className={`bg-white p-6 rounded-xl shadow-lg ${className}`}>{title && <h2 className="text-2xl font-bold text-stone-800 mb-4 border-b pb-2">{title}</h2>}{children}</div> );
const InputField = ({ label, id, type = 'text', value, onChange, placeholder, required = false, name, ...props }) => ( /* Giữ nguyên */ <div className="mb-4"><label htmlFor={id} className="block text-sm font-medium text-stone-700 mb-1">{label}</label><input type={type} id={id} name={name || id} value={value || ''} onChange={onChange} placeholder={placeholder} required={required} className="w-full p-3 border border-gray-300 rounded-lg focus:ring-rose-500 focus:border-rose-500 shadow-sm" {...props} /></div> );

// --- Các Component Xác thực (Giữ nguyên logic, không cần header) ---

const AuthForm = ({ title, route, state, setState, appState }) => { /* Giữ nguyên logic như code bạn gửi lần trước */ const isLogin=route==='login'; const{login,notify,setLoading,loading,setRoute}=appState; const[error,setError]=useState(''); const onSubmit=async(e)=>{e.preventDefault(); setLoading(true); setError(''); try{const payload=isLogin?{email:state.identifier,password:state.password}:{username:state.username,email:state.email,password:state.password,role:'user'}; const response=await axios.post(isLogin?'/login':'/register',payload); if(isLogin){if(response.data.token&&response.data.user){login(response.data.user,response.data.token); notify('Đăng nhập thành công!','success');}else{throw new Error('Phản hồi không hợp lệ');}}else{notify('Đăng ký thành công! Vui lòng đăng nhập.','success'); setRoute('login');}}catch(err){console.error('Lỗi xác thực:',err.response?.data||err.message); const message=err.response?.data?.msg||err.message||'Xác thực thất bại.'; setError(message); notify(message,'error');}finally{setLoading(false);}}; return(<Card title={title} className="max-w-md mx-auto my-12"><form onSubmit={onSubmit}>{error&&<div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded">{error}</div>}{!isLogin&&<InputField label="Username" id="username" name="username" value={state.username} onChange={(e)=>setState({...state,username:e.target.value})} required/>}<InputField label={"Email"} id={isLogin?"identifier":"email"} name={isLogin?"identifier":"email"} type="email" value={isLogin?state.identifier:state.email} onChange={(e)=>setState({...state,[isLogin?'identifier':'email']:e.target.value})} required/><InputField label="Password" id="password" name="password" type="password" value={state.password} onChange={(e)=>setState({...state,password:e.target.value})} required/><button type="submit" disabled={loading} className="w-full bg-rose-600 text-white p-3 rounded-lg font-semibold hover:bg-rose-700 disabled:bg-rose-400">{loading?'Đang xử lý...':title}</button></form><p className="mt-4 text-center text-sm text-stone-500">{isLogin?"Chưa có tài khoản? ":"Đã có tài khoản? "}<button onClick={()=>setRoute(isLogin?'register':'login')} className="text-rose-600 hover:text-rose-800 font-medium">{isLogin?'Đăng ký':'Đăng nhập'}</button></p></Card>);};
const RegisterPage = ({ appState }) => { const [form, setForm] = useState({ username: '', email: '', password: '' }); return <AuthForm title="Tạo tài khoản" route="register" state={form} setState={setForm} appState={appState} />; };
const LoginPage = ({ appState }) => { const [form, setForm] = useState({ identifier: '', password: '' }); return <AuthForm title="Đăng nhập" route="login" state={form} setState={setForm} appState={appState} />; };

// --- Component Trang Profile ---

const ProfilePage = ({ appState }) => {
    const { user, notify, fetchProfile, isAdmin, isLibrarianOrAdmin, token, logout, loading, setLoading } = appState;
    const [file, setFile] = useState(null);

    // Fetch profile khi component mount nếu có token
    useEffect(() => {
        if (token) { fetchProfile(token); }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [token]); // Chỉ fetch khi token thay đổi

    const handleFileChange = (e) => { setFile(e.target.files[0]); };

    // Handle Upload - GỬI TOKEN TRONG HEADER
    const handleUpload = async () => {
        if (!file || !token) { notify('Vui lòng chọn ảnh và đăng nhập.', 'error'); return; }
        setLoading(true); const formData = new FormData(); formData.append('avatar', file);
        try {
            await axios.post('/profile/avatar', formData, { headers: { 'Content-Type': 'multipart/form-data', 'Authorization': `Bearer ${token}` } });
            notify('Cập nhật avatar thành công!', 'success'); setFile(null);
            fetchProfile(token); // Fetch lại profile
        } catch (error) {
            console.error("Lỗi upload avatar:", error.response?.data || error.message);
            notify(error.response?.data?.msg || 'Lỗi upload avatar.', 'error');
            if (error.response && (error.response.status === 401 || error.response.status === 422)) { logout(); }
        } finally { setLoading(false); }
    };

    // Handle Return - GỬI TOKEN TRONG HEADER
    const handleReturn = async (borrowingId, title) => {
        if (!token) { notify('Yêu cầu xác thực.', 'error'); return; }
        if (!window.confirm(`Trả sách "${title}"?`)) return;
        try {
            await axios.post(`/return/${borrowingId}`, {}, { headers: { 'Authorization': `Bearer ${token}` } });
            notify(`Đã trả sách '${title}'!`, 'success');
            fetchProfile(token); // Fetch lại profile
        } catch (error) {
            console.error("Lỗi trả sách:", error.response?.data || error.message);
            notify(error.response?.data?.msg || 'Lỗi trả sách.', 'error');
            if (error.response && (error.response.status === 401 || error.response.status === 422)) { logout(); }
        }
    };

    if (loading && !user) return <p className="text-center text-xl mt-12 text-stone-600">Đang tải...</p>;
    if (!user) return <p className="text-center text-xl mt-12 text-stone-600">Vui lòng đăng nhập.</p>;

    return ( <div className="container mx-auto px-4 py-8 grid lg:grid-cols-3 gap-8"> <Card className="lg:col-span-1 h-fit"> {/* Thông tin User */} <div className="flex flex-col items-center"> <div className="relative w-32 h-32 rounded-full overflow-hidden mb-4 border-4 border-rose-500 shadow-lg"> <img src={user.avatar_url || 'https://placehold.co/128x128/E879F9/FFFFFF?text=P'} alt="Avatar" className="object-cover w-full h-full" onError={(e) => { e.target.onerror = null; e.target.src="https://placehold.co/128x128/E879F9/FFFFFF?text=Err" }}/> </div> <h3 className="text-3xl font-bold text-stone-900">{user.username}</h3> <p className={`text-sm font-semibold mt-1 py-1 px-3 rounded-full ${isAdmin ? 'bg-red-100 text-red-600' : isLibrarianOrAdmin ? 'bg-rose-100 text-rose-600' : 'bg-gray-100 text-stone-600'}`}> {user.role?.toUpperCase() || 'USER'} </p> <div className="mt-6 w-full space-y-2 text-stone-600"> <p className="flex justify-between items-center border-b pb-1">Email: <span className="font-medium text-stone-800 break-all">{user.email}</span></p> <p className="flex justify-between items-center">Ngày tham gia: <span className="font-medium text-stone-800">{user.created_at ? new Date(user.created_at).toLocaleDateString() : 'N/A'}</span></p> <p className="text-xs break-words pt-4 text-center text-stone-400">User ID: {user.id}</p> </div> </div> </Card> <div className="lg:col-span-2 space-y-8"> {/* Upload và Lịch sử */} <Card title="Đổi Avatar"> <div className="flex flex-col sm:flex-row space-y-4 sm:space-y-0 sm:space-x-4 items-center"> <input type="file" onChange={handleFileChange} accept="image/*" className="block w-full text-sm text-stone-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-rose-50 file:text-rose-700 hover:file:bg-rose-100" /> <button onClick={handleUpload} disabled={!file || loading || !token} className="w-full sm:w-auto flex-shrink-0 bg-emerald-600 text-white p-3 rounded-lg font-semibold hover:bg-emerald-700 disabled:bg-emerald-400"> {loading ? 'Đang tải lên...' : 'Lưu Avatar'} </button> </div> <p className="mt-2 text-sm text-stone-500">Tối đa 2MB. PNG, JPG, JPEG, GIF.</p> </Card> <Card title="Lịch sử mượn sách"> {!user.borrowing_history || user.borrowing_history.length === 0 ? ( <p className="text-stone-500">Chưa mượn sách nào.</p> ) : ( <div className="space-y-4 max-h-96 overflow-y-auto pr-2"> {user.borrowing_history.map((item) => ( <div key={item.id} className="p-4 border border-gray-100 rounded-xl shadow-sm hover:shadow-md flex justify-between items-center bg-gray-50"> <div> <p className="font-semibold text-stone-900">{item.book_title}</p> <p className="text-sm text-stone-600">Mượn: {new Date(item.borrow_date).toLocaleDateString()}</p> <p className="text-sm text-red-500 font-medium">Hạn trả: {new Date(item.due_date).toLocaleDateString()}</p> </div> <div className="flex items-center space-x-3"> <span className={`px-3 py-1 text-xs font-semibold rounded-full ${ item.status === 'returned' ? 'bg-emerald-100 text-emerald-600' : (new Date(item.due_date) < new Date() && item.status !== 'returned' ? 'bg-red-100 text-red-600' : 'bg-yellow-100 text-yellow-600')}`}> {item.status?.toUpperCase() || 'UNKNOWN'} </span> {item.status === 'borrowed' && ( <button onClick={() => handleReturn(item.id, item.book_title)} className="p-2 bg-rose-500 text-white rounded-full hover:bg-rose-600 shadow-md" title="Trả sách"> <RotateCcw className="w-5 h-5" /> </button> )} </div> </div> ))} </div> )} </Card> </div> </div> );
};


// --- Component BookModal (ĐỊNH NGHĨA NGOÀI) ---
const BookModal = ({ appState, currentBook, form, handleInputChange, handleSubmit, handleCloseModal }) => {
    const { loading } = appState;
    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50 p-4 overflow-y-auto">
            <Card title={currentBook ? "Sửa thông tin sách" : "Thêm sách mới"} className="w-full max-w-lg my-8 transform transition-all scale-100 opacity-100">
                <form onSubmit={handleSubmit}>
                    <InputField label="Tiêu đề" id="title" name="title" value={form.title} onChange={handleInputChange} required />
                    <InputField label="Tác giả" id="author" name="author" value={form.author} onChange={handleInputChange} required />
                    <InputField label="ISBN" id="isbn" name="isbn" value={form.isbn} onChange={handleInputChange} />
                    <div className="grid grid-cols-2 gap-4">
                        <InputField label="Năm XB" id="published_year" name="published_year" type="number" value={form.published_year} onChange={handleInputChange} />
                        <InputField label="Thể loại" id="genre" name="genre" value={form.genre} onChange={handleInputChange} />
                    </div>
                    <InputField label="Số lượng" id="stock" name="stock" type="number" value={form.stock} onChange={handleInputChange} required min="0" />
                    <div className="flex justify-end space-x-4 mt-6">
                        <button type="button" onClick={handleCloseModal} className="px-5 py-2 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-100 transition focus:outline-none focus:ring-2 focus:ring-gray-400">Hủy</button>
                        <button type="submit" disabled={loading} className="flex items-center space-x-2 px-5 py-2 bg-rose-600 text-white rounded-lg hover:bg-rose-700 transition disabled:bg-rose-400 focus:outline-none focus:ring-2 focus:ring-rose-500 focus:ring-offset-2"> <Save className="w-5 h-5" /> <span>{loading ? 'Đang lưu...' : 'Lưu sách'}</span> </button>
                    </div>
                </form>
            </Card>
        </div>
    );
};


// --- Component Quản lý Sách (Chỉ Admin/Librarian) ---
const BookManager = ({ appState, books, fetchBooks }) => {
  const { isLibrarianOrAdmin, isAdmin, notify, setLoading, loading, token, logout } = appState;
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentBook, setCurrentBook] = useState(null);
  const [form, setForm] = useState({ title: '', author: '', isbn: '', published_year: '', stock: 1, genre: '' });

  // Cập nhật form khi currentBook thay đổi
  useEffect(() => {
    if (currentBook) { setForm({ title: currentBook.title || '', author: currentBook.author || '', isbn: currentBook.isbn || '', published_year: currentBook.published_year || '', stock: currentBook.stock >= 0 ? currentBook.stock : 0, genre: currentBook.genre || '' }); }
    else { setForm({ title: '', author: '', isbn: '', published_year: '', stock: 1, genre: '' }); }
  }, [currentBook]);

  if (!isLibrarianOrAdmin) return null; // Không render nếu không có quyền

  const handleOpenModal = (book = null) => { setCurrentBook(book); setIsModalOpen(true); };
  const handleCloseModal = () => { setIsModalOpen(false); setCurrentBook(null); };

  const handleInputChange = (e) => {
    const { name, value, type } = e.target;
    setForm(prev => ({ ...prev, [name]: type === 'number' ? (parseInt(value) >= 0 ? parseInt(value) : 0) : value })); // Đảm bảo số >= 0
  };

  // Hàm Submit (Thêm/Sửa sách) - GỬI TOKEN TRONG HEADER
  const handleSubmit = async (e) => {
    e.preventDefault(); if (!token) { notify('Yêu cầu xác thực.', 'error'); return; }
    setLoading(true);
    try {
        const config = { headers: { 'Authorization': `Bearer ${token}` } }; // Token header
        if (currentBook) { await axios.put(`/books/${currentBook.id}`, form, config); notify('Cập nhật sách thành công!', 'success'); }
        else { await axios.post('/books', form, config); notify('Thêm sách thành công!', 'success'); }
        fetchBooks(token); // Tải lại danh sách sách, **truyền token**
        handleCloseModal();
    } catch (error) {
        console.error("Lỗi lưu sách:", error.response?.data || error.message);
        notify(error.response?.data?.msg || 'Lỗi lưu dữ liệu sách.', 'error');
        if (error.response && (error.response.status === 401 || error.response.status === 422)) { logout(); }
    } finally { setLoading(false); }
  };

  // Hàm Delete - GỬI TOKEN TRONG HEADER
  const handleDelete = async (bookId, title) => {
    if (!isAdmin) { notify('Chỉ Admin mới có thể xóa sách.', 'error'); return; }
    if (!token) { notify('Yêu cầu xác thực.', 'error'); return; }
    if (!window.confirm(`Xóa sách "${title}"?`)) return;
    setLoading(true);
    try {
        // GỬI TOKEN TRONG HEADER
        await axios.delete(`/books/${bookId}`, { headers: { 'Authorization': `Bearer ${token}` } });
        notify('Xóa sách thành công!', 'success');
        fetchBooks(token); // Tải lại danh sách sách, **truyền token**
    } catch (error) {
        console.error("Lỗi xóa sách:", error.response?.data || error.message);
        notify(error.response?.data?.msg || 'Lỗi xóa sách.', 'error');
        if (error.response && (error.response.status === 401 || error.response.status === 422)) { logout(); }
    } finally { setLoading(false); }
  };

  return (
    <>
      {isModalOpen && <BookModal appState={appState} currentBook={currentBook} form={form} handleInputChange={handleInputChange} handleSubmit={handleSubmit} handleCloseModal={handleCloseModal} />}
      <div className="flex justify-between items-center mb-6"> <h2 className="text-3xl font-bold text-stone-800">Quản lý sách</h2> <button onClick={() => handleOpenModal()} className="flex items-center space-x-2 px-4 py-2 bg-rose-600 text-white rounded-lg font-semibold hover:bg-rose-700 transition shadow-md"> <Plus className="w-5 h-5" /> <span>Thêm sách mới</span> </button> </div>
      <div className="overflow-x-auto bg-white rounded-xl shadow-lg">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50"> <tr> <th className="th">Tiêu đề</th> <th className="th">Tác giả</th> <th className="th">ISBN</th> <th className="th text-center">Trong kho</th> <th className="th text-center">Có sẵn</th> <th className="th text-center">Hành động</th> </tr> </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {Array.isArray(books) && books.length > 0 ? books.map((book) => (
              <tr key={book.id} className="hover:bg-gray-50 transition duration-150">
                <td className="td font-medium text-stone-900">{book.title}</td> <td className="td text-stone-500">{book.author}</td> <td className="td text-stone-500">{book.isbn || 'N/A'}</td> <td className="td text-center text-stone-900">{book.stock}</td> <td className={`td text-center font-bold ${book.available_copies > 0 ? 'text-emerald-600' : 'text-red-500'}`}> {book.available_copies} </td>
                <td className="td text-sm font-medium flex justify-center space-x-3">
                  <button onClick={() => handleOpenModal(book)} className="text-rose-600 hover:text-rose-900 p-1 rounded-full hover:bg-rose-50" title="Sửa"><Settings className="w-5 h-5" /></button>
                  <button onClick={() => handleDelete(book.id, book.title)} disabled={!isAdmin || loading} className={`p-1 rounded-full transition ${isAdmin ? 'text-red-600 hover:text-red-900 hover:bg-red-50' : 'text-stone-400 cursor-not-allowed'} ${loading ? 'opacity-50 cursor-wait' : ''}`} title="Xóa"><Trash2 className="w-5 h-5" /></button>
                </td>
              </tr>
            )) : ( <tr> <td colSpan="6" className="px-6 py-4 text-center text-stone-500">{loading ? 'Đang tải...' : 'Không có sách nào.'}</td> </tr> )}
          </tbody>
        </table>
      </div>
      {/* CSS Helper classes */}
      <style>{`.th { padding: 0.75rem 1.5rem; text-align: left; font-size: 0.75rem; font-weight: 500; color: #71717a; text-transform: uppercase; letter-spacing: 0.05em; } .td { padding: 1rem 1.5rem; white-space: nowrap; font-size: 0.875rem; }`}</style>
    </>
  );
};


// --- Component Trang Dashboard (Hiển thị danh sách sách) ---
const DashboardPage = ({ appState }) => {
  const { isLibrarianOrAdmin, notify, loading, setLoading, token, logout } = appState;
  const [books, setBooks] = useState([]);
  const [error, setError] = useState(null);

  // Hàm fetchBooks ổn định, nhận token và xử lý lỗi logout
  const fetchBooks = useCallback(async (authToken) => {
    if (!authToken) { setError("Yêu cầu xác thực."); setBooks([]); setLoading(false); return; }
    setLoading(true); setError(null);
    try {
      // GỬI TOKEN TRONG HEADER
      const res = await axios.get('/books', { headers: { 'Authorization': `Bearer ${authToken}` } });
      setBooks(Array.isArray(res.data) ? res.data : []);
    } catch (error) {
      console.error('Lỗi fetch sách:', error.response?.data || error.message);
      const errorMessage = error.response?.data?.msg || 'Không thể tải sách.';
      setError(errorMessage); notify(errorMessage, 'error');
      if (error.response && (error.response.status === 401 || error.response.status === 422)) { logout(); }
    } finally { setLoading(false); }
  }, [setLoading, notify, logout]); // Dependencies ổn định

  // useEffect gọi fetchBooks khi token thay đổi hoặc component mount
  useEffect(() => {
    fetchBooks(token); // Gọi với token hiện tại (có thể null)
  }, [token, fetchBooks]);

  // Hàm mượn sách - GỬI TOKEN TRONG HEADER
  const handleBorrow = async (bookId, title) => {
    if (!token) { notify('Vui lòng đăng nhập.', 'error'); return; }
    if (!window.confirm(`Mượn sách "${title}"?`)) return;
    // Tạm thời quản lý loading cục bộ cho nút borrow để không block toàn trang
    const buttonElement = document.getElementById(`borrow-btn-${bookId}`);
    if (buttonElement) buttonElement.disabled = true; // Disable nút
    try {
        // GỬI TOKEN TRONG HEADER
        const res = await axios.post(`/borrow/${bookId}`, {}, { headers: { 'Authorization': `Bearer ${token}` } });
        notify(res.data.msg, 'success');
        fetchBooks(token); // Tải lại sách, truyền token
    } catch (error) {
        console.error("Lỗi mượn sách:", error.response?.data || error.message);
        notify(error.response?.data?.msg || 'Lỗi mượn sách.', 'error');
        if (error.response && (error.response.status === 401 || error.response.status === 422)) { logout(); }
    } finally {
        if (buttonElement) buttonElement.disabled = false; // Re-enable nút
    }
  };

    // --- Render Giao diện ---
    if (error && !token) { return ( <div className="text-center py-8"> <p className="text-xl text-red-600">{error || "Vui lòng đăng nhập."}</p> <button onClick={() => appState.setRoute('login')} className="mt-4 px-4 py-2 bg-rose-600 text-white rounded hover:bg-rose-700">Đăng nhập</button> </div> ); }
    if (error && token) { return ( <div className="text-center py-8"> <p className="text-xl text-red-600">{error}</p> <button onClick={() => fetchBooks(token)} className="mt-4 px-4 py-2 bg-rose-600 text-white rounded hover:bg-rose-700">Thử lại</button> </div> ); }
    // Chỉ hiển thị loading nếu không có lỗi và chưa có sách
    if (loading && books.length === 0) { return <p className="text-center text-xl mt-12 text-rose-600">Đang tải sách...</p>; }


  // Render nội dung chính
  return (
    <div className="container mx-auto px-4 py-8">
      {isLibrarianOrAdmin && <BookManager appState={appState} books={books} fetchBooks={fetchBooks} />}
      <h2 className={`text-3xl font-bold text-stone-800 ${isLibrarianOrAdmin ? 'mt-12 border-t pt-8' : 'mt-8'}`}> Danh mục sách </h2>
      {!Array.isArray(books) || books.length === 0 ? (
        !loading && !error && <p className="text-center text-stone-600 mt-8">Không có sách nào.</p>
      ) : (
        <div className="mt-6 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {books.map((book) => (
            <Card key={book.id} className="flex flex-col justify-between hover:shadow-rose-100 transition duration-300 ease-in-out">
              <div> <h3 className="text-xl font-semibold text-rose-700">{book.title}</h3> <p className="text-sm text-stone-600 italic">bởi {book.author}</p> <p className="text-xs mt-2 bg-stone-100 text-stone-700 px-2 py-1 rounded-md w-fit"> Thể loại: {book.genre || 'N/A'} </p> </div>
              <div className="mt-4 pt-4 border-t"> <p className="text-sm font-medium text-stone-800"> Có sẵn: <span className={book.available_copies > 0 ? 'text-emerald-600' : 'text-red-500'}> {book.available_copies} / {book.stock} </span> </p>
                <button id={`borrow-btn-${book.id}`} onClick={() => handleBorrow(book.id, book.title)} disabled={book.available_copies === 0 || !token} className={`mt-3 w-full p-2 bg-rose-600 text-white rounded-lg font-medium hover:bg-rose-700 transition duration-150 disabled:bg-gray-400 disabled:cursor-not-allowed`} >
                  {token ? (book.available_copies === 0 ? 'Hết sách' : 'Mượn sách') : 'Đăng nhập để mượn'}
                </button>
              </div>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
};


// --- Component Trang Chủ ---
const HomePage = () => ( /* Giữ nguyên */ <div className="container mx-auto px-4 py-16 text-center"><div className="max-w-3xl mx-auto"><h1 className="text-5xl font-extrabold text-stone-900 mb-4">Hệ thống Quản lý Thư viện Nâng cao</h1><p className="text-xl text-stone-600 mb-8">Giải pháp hiện đại...</p><div className="grid md:grid-cols-3 gap-6 mt-10"><FeatureCard icon={User} title="Bảo mật theo vai trò" description="Phân quyền..." /><FeatureCard icon={BookOpen} title="Danh mục thời gian thực" description="Xem tình trạng..." /><FeatureCard icon={Settings} title="Hồ sơ nâng cao" description="Hồ sơ người dùng..." /></div></div></div> );
const FeatureCard = ({ icon: Icon, title, description }) => ( /* Giữ nguyên */ <Card className="flex flex-col items-center text-center p-6 bg-rose-50 border-t-4 border-rose-600 hover:shadow-lg transition duration-300"><Icon className="w-10 h-10 text-rose-600 mb-3" /><h3 className="text-xl font-semibold text-stone-800 mb-2">{title}</h3><p className="text-stone-600">{description}</p></Card> );

// --- Các Component "Giữ chỗ" cho Admin ---
const UserManagementPage = ({ appState }) => ( /* Giữ nguyên */ <div className="container mx-auto p-8"><h1 className="text-3xl font-bold text-stone-800">Quản lý người dùng</h1><p className="mt-4 text-stone-600">(Sắp ra mắt...)</p></div> );
const SettingsPage = ({ appState }) => ( /* Giữ nguyên */ <div className="container mx-auto p-8"><h1 className="text-3xl font-bold text-stone-800">Cài đặt</h1><p className="mt-4 text-stone-600">(Sắp ra mắt...)</p></div> );

// --- Component App Chính ---
const App = () => {
  const appState = useAppState();
  const { route, token, user, isAdmin, logout } = appState;

  // Logic render nội dung chính, xử lý routing chuẩn
  const renderContent = useMemo(() => {
    // ---- Khi CHƯA ĐĂNG NHẬP ----
    if (!token) {
        switch (route) {
            case 'login': return <LoginPage appState={appState} />;
            case 'register': return <RegisterPage appState={appState} />;
            default: return <HomePage />; // Mọi route khác về home
        }
    }
    // ---- Khi ĐÃ ĐĂNG NHẬP ----
    switch (route) {
      case 'profile': return <ProfilePage appState={appState} />;
      case 'dashboard': return isAdmin ? <AdminDashboard appState={appState} /> : <DashboardPage appState={appState} />; // Admin dashboard hoặc user dashboard (sách)
      case 'books': return <DashboardPage appState={appState} />; // Luôn là trang sách
      // Admin routes - Redirect non-admin về trang sách
      case 'users': return isAdmin ? <UserManagementPage appState={appState} /> : <DashboardPage appState={appState} />;
      case 'settings': return isAdmin ? <SettingsPage appState={appState} /> : <DashboardPage appState={appState} />;
      case 'home': return <HomePage />;
      // Default khi đã đăng nhập: về dashboard tương ứng
      default: return isAdmin ? <AdminDashboard appState={appState} /> : <DashboardPage appState={appState} />;
    }
  // Bỏ 'user' khỏi dependency array vì 'isAdmin' đã phụ thuộc vào 'user'
  }, [route, token, isAdmin, appState]); // Dependencies chuẩn

  return (
    <div className="min-h-screen bg-stone-100 font-sans">
      <Header user={user} setRoute={appState.setRoute} logout={logout} />
      <main className="pb-16"> {renderContent} </main>
      <Notification message={appState.message} />
    </div>
  );
};

// --- Component Admin Dashboard ---
const AdminDashboard = ({ appState }) => {
  // Redirect nếu không phải admin
  if (!appState.isAdmin) return <DashboardPage appState={appState} />;
  return (
    <div className="container mx-auto p-8 space-y-6">
      <h1 className="text-3xl font-bold text-stone-800">Admin Dashboard</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Sửa onClick để setRoute('books') */}
        <DashboardCard title="Quản lý sách" icon={<BookOpen className="h-8 w-8 text-rose-600" />} onClick={() => appState.setRoute('books')} />
        <DashboardCard title="Quản lý người dùng" icon={<User className="h-8 w-8 text-rose-600" />} onClick={() => appState.setRoute('users')} />
        <DashboardCard title="Cài đặt" icon={<Settings className="h-8 w-8 text-rose-600" />} onClick={() => appState.setRoute('settings')} />
      </div>
    </div>
  );
};

// Component Card nhỏ cho Admin Dashboard
const DashboardCard = ({ title, icon, onClick }) => ( /* Giữ nguyên */ <button onClick={onClick} className="p-6 bg-white rounded-xl shadow-lg hover:shadow-xl transition-shadow duration-200 text-left w-full border-l-4 border-rose-500 hover:border-rose-700 focus:outline-none focus:ring-2 focus:ring-rose-500 focus:ring-opacity-50" > <div className="flex items-center gap-4"> {icon} <h3 className="text-lg font-semibold text-stone-900">{title}</h3> </div> </button> );

export default App;
