import React, { useState, useEffect, useCallback, useMemo } from 'react';
import axios from 'axios';
import { LogIn, User, BookOpen, Settings, Home, Plus, Trash2, RotateCcw, Save } from 'lucide-react';

// Configure Axios to point to the Flask backend
const API_BASE_URL = 'http://localhost:5001/api'; // Make sure this matches your Flask port
axios.defaults.baseURL = API_BASE_URL;
axios.defaults.headers.common['Content-Type'] = 'application/json';

// --- Global Setup for Tailwind CDN ---
(function() {
  if (!document.getElementById('tailwind-cdn')) {
    const script = document.createElement('script');
    script.src = 'https://cdn.tailwindcss.com';
    script.id = 'tailwind-cdn';
    document.head.appendChild(script);
  }
})();

// --- Utility Functions and Hooks ---

// Hook to manage global application state
const useAppState = () => {
    const [user, setUser] = useState(() => {
        try {
            const savedUser = localStorage.getItem('user_data');
            // Handle potential JSON parsing errors
            return savedUser && savedUser !== 'undefined' ? JSON.parse(savedUser) : null;
        } catch (e) {
            console.error("Failed to parse user_data from localStorage", e);
            localStorage.removeItem('user_data'); // Clear corrupted data
            return null;
        }
    });
    const [token, setToken] = useState(() => {
      const storedToken = localStorage.getItem('jwt_token');
      // Ensure token is not null or the string "null" or "undefined"
      return (storedToken && storedToken !== 'null' && storedToken !== 'undefined') ? storedToken : null;
    });
    const [route, setRoute] = useState('home');
    const [message, setMessage] = useState({ text: '', type: '' });
    const [loading, setLoading] = useState(false);

    // --- REMOVED: Global Axios Header Setup ---
    // Removed the useEffect that globally sets axios headers based on token.
    // We will add headers manually to each authenticated request.

    // Persist user data to localStorage when it changes
    useEffect(() => {
        if (user) {
            localStorage.setItem('user_data', JSON.stringify(user));
        } else {
             // If user becomes null (e.g., after logout), remove from storage
            localStorage.removeItem('user_data');
        }
    }, [user]);

    // Notification handler
    const notify = useCallback((text, type = 'success') => {
        setMessage({ text, type });
        setTimeout(() => setMessage({ text: '', type: '' }), 4000);
    }, []); // setMessage is stable

    // Login handler
    const login = useCallback((userData, jwtToken) => {
        console.log('Login called with:', { userData, jwtToken });
        localStorage.setItem('jwt_token', jwtToken);
        localStorage.setItem('user_data', JSON.stringify(userData));
        setToken(jwtToken);
        setUser(userData);
        // --- REMOVED: Global Axios Header Setup ---
        setRoute('dashboard'); // Redirect after login
    }, []); // setToken, setUser, setRoute are stable

    // Logout handler
    const logout = useCallback(() => {
        localStorage.removeItem('jwt_token');
        localStorage.removeItem('user_data');
        // --- REMOVED: Global Axios Header Setup ---
        setToken(null);
        setUser(null);
        setRoute('home');
    }, []); // setToken, setUser, setRoute are stable

    // Fetch user profile data
    const fetchProfile = useCallback(async (authToken) => { // Accept token as argument
        if (!authToken) {
            console.log("fetchProfile: No token provided.");
            return;
        }
        setLoading(true);
        try {
            // Add header manually
            const res = await axios.get('/profile', {
                headers: { 'Authorization': `Bearer ${authToken}` }
            });
            const newUserData = res.data; // Assume backend returns user data directly

            // Use functional update for safety
            setUser(prevUser => ({
                ...prevUser,
                ...newUserData,
            }));
             // Let the useEffect handle localStorage persistence
        } catch (error) {
            console.error('Failed to fetch profile:', error);
            if (error.response && (error.response.status === 401 || error.response.status === 422)) { // 422 for invalid token
                notify('Session invalid or expired. Please log in again.', 'error');
                logout(); // Automatically log out on auth errors
            } else {
                notify('Failed to load profile data.', 'error');
            }
        } finally {
            setLoading(false);
        }
    }, [logout, notify]); // Dependencies: only stable functions

    // Utility functions to check user role
    const isAdmin = useMemo(() => user?.role === 'admin', [user]);
    const isLibrarian = useMemo(() => user?.role === 'librarian', [user]);
    const isLibrarianOrAdmin = useMemo(() => isAdmin || isLibrarian, [isAdmin, isLibrarian]);

    return {
        user,
        token,
        route,
        setRoute,
        login,
        logout,
        message,
        loading,
        setLoading,
        notify,
        fetchProfile,
        isAdmin,
        isLibrarianOrAdmin
    };
};

// --- General Components ---

const Notification = ({ message }) => {
  if (!message.text) return null;
  const classes = message.type === 'success'
    ? 'bg-emerald-500 border-emerald-700'
    : 'bg-red-500 border-red-700';

  return (
    <div className={`fixed top-4 right-4 z-50 p-4 rounded-lg text-white font-semibold shadow-xl transition-all duration-300 ${classes}`}>
      {message.text}
    </div>
  );
};

const Header = ({ user, setRoute, logout }) => {
  // Debug log removed for brevity
  return (
    <header className="bg-stone-900 shadow-xl text-white sticky top-0 z-40"> {/* Changed gray to stone */}
      <div className="container mx-auto px-4 py-4 flex justify-between items-center">
        <div className="flex items-center space-x-4 cursor-pointer" onClick={() => setRoute('home')}>
          <BookOpen className="h-7 w-7 text-rose-400" />
          <h1 className="text-2xl font-bold text-rose-100">LMS Pro</h1>
        </div>

        <nav className="flex items-center space-x-4">
          <NavItem icon={Home} label="Home" onClick={() => setRoute('home')} />

          {user ? (
            <>
              <NavItem icon={BookOpen} label="Books" onClick={() => setRoute('books')} /> {/* Changed route to 'books' */}
              <NavItem icon={User} label="Profile" onClick={() => setRoute('profile')} />
              <button
                onClick={logout}
                className="flex items-center space-x-2 px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
              >
                <LogIn className="h-5 w-5 rotate-180" />
                <span>Logout ({user.username})</span>
              </button>
            </>
          ) : (
            <>
              <NavItem icon={LogIn} label="Login" onClick={() => setRoute('login')} />
              <NavItem icon={User} label="Register" onClick={() => setRoute('register')} />
            </>
          )}
        </nav>
      </div>
    </header>
  );
};

const NavItem = ({ icon: Icon, label, onClick }) => (
  <button
    onClick={onClick}
    className="flex items-center space-x-2 px-3 py-2 rounded-lg text-stone-300 hover:bg-stone-800 hover:text-white transition-colors" // Changed gray to stone
  >
    <Icon className="h-5 w-5" />
    <span>{label}</span>
  </button>
);

const Card = ({ title, children, className = '' }) => (
  <div className={`bg-white p-6 rounded-xl shadow-2xl transition-all duration-300 ${className}`}>
    {title && <h2 className="text-2xl font-bold text-stone-800 mb-4 border-b pb-2">{title}</h2>}
    {children}
  </div>
);

const InputField = ({ label, id, type = 'text', value, onChange, placeholder, required = false, name }) => (
  <div className="mb-4">
    <label htmlFor={id} className="block text-sm font-medium text-stone-700 mb-1">
      {label}
    </label>
    <input
      type={type}
      id={id}
      name={name || id}
      // Ensure value is never null/undefined for controlled inputs
      value={value || ''}
      onChange={onChange}
      placeholder={placeholder}
      required={required}
      className="w-full p-3 border border-gray-300 rounded-lg focus:ring-rose-500 focus:border-rose-500 shadow-sm transition duration-150"
    />
  </div>
);

// --- Auth Components ---

const AuthForm = ({ title, route, state, setState, appState }) => {
  const isLogin = route === 'login';
  // Get only needed functions from appState
  const { login, notify, setLoading, loading, setRoute } = appState;
  const [error, setError] = useState('');

  const onSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const payload = isLogin ? {
        email: state.identifier, // Assuming login uses email
        password: state.password
      } : {
        username: state.username,
        email: state.email,
        password: state.password,
        role: 'user' // Default role for registration
      };
      const response = await axios.post(isLogin ? '/login' : '/register', payload);

      console.log('Server response:', response.data);

      if (isLogin) {
        if (response.data.token && response.data.user) {
          // login function from appState handles state update and localStorage
          login(response.data.user, response.data.token);
          notify('Login successful!', 'success');
          // No need to setRoute here, login function does it
        } else {
          // Throw error if response is missing expected data
          throw new Error('Invalid server response during login');
        }
      } else {
        // Registration success
        notify('Registration successful! Please log in.', 'success');
        setRoute('login'); // Redirect to login page after registration
      }
    } catch (err) { // Changed variable name to err
      console.error('Auth error:', err.response?.data || err.message);
      const message = err.response?.data?.msg || err.message || 'Authentication failed.';
      setError(message);
      notify(message, 'error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Card title={title} className="max-w-md mx-auto my-12">
      <form onSubmit={onSubmit}>
        {error && (
          <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded">
            {error}
          </div>
        )}
        {!isLogin && (
          <InputField
            label="Username"
            id="username"
            value={state.username}
            onChange={(e) => setState({ ...state, username: e.target.value })}
            required
          />
        )}
        <InputField
          label={"Email"} // Always Email
          id={isLogin ? "identifier" : "email"}
          type="email"
          value={isLogin ? state.identifier : state.email}
          onChange={(e) => setState({ ...state, [isLogin ? 'identifier' : 'email']: e.target.value })}
          required
        />
        <InputField
          label="Password"
          id="password"
          type="password"
          value={state.password}
          onChange={(e) => setState({ ...state, password: e.target.value })}
          required
        />
        <button
          type="submit"
          disabled={loading} // Use loading from appState
          className="w-full bg-rose-600 text-white p-3 rounded-lg font-semibold hover:bg-rose-700 transition duration-200 shadow-md disabled:bg-rose-400"
        >
          {loading ? 'Processing...' : title}
        </button>
      </form>
      <p className="mt-4 text-center text-sm text-stone-500">
        {isLogin ? "Don't have an account? " : "Already have an account? "}
        <button onClick={() => setRoute(isLogin ? 'register' : 'login')} className="text-rose-600 hover:text-rose-800 font-medium">
          {isLogin ? 'Register here' : 'Login here'}
        </button>
      </p>
    </Card>
  );
};

const RegisterPage = ({ appState }) => {
  const [form, setForm] = useState({ username: '', email: '', password: '' });
  return <AuthForm title="Create Account" route="register" state={form} setState={setForm} appState={appState} />;
};

const LoginPage = ({ appState }) => {
  const [form, setForm] = useState({ identifier: '', password: '' });
  return <AuthForm title="Sign In" route="login" state={form} setState={setForm} appState={appState} />;
};

// --- Profile Component ---

const ProfilePage = ({ appState }) => {
    // Get token directly for API calls within this component
    const { user, notify, fetchProfile, isAdmin, isLibrarianOrAdmin, token } = appState;
    const [file, setFile] = useState(null);
    const [isUploading, setIsUploading] = useState(false);

    // Auto-fetch profile data when entering the page OR when token changes
    useEffect(() => {
        if (token) {
            fetchProfile(token); // Pass token to fetchProfile
        }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [token]); // Run only when token changes, fetchProfile dependency removed

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleUpload = async () => {
        if (!file || !token) { // Check for token
            notify('Please select an image and ensure you are logged in.', 'error');
            return;
        }

        setIsUploading(true);
        const formData = new FormData();
        formData.append('avatar', file);

        try {
            // Add header manually
            await axios.post('/profile/avatar', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    'Authorization': `Bearer ${token}`
                },
            });
            notify('Avatar updated successfully!', 'success');
            setFile(null);
            fetchProfile(token); // Refresh profile data after upload
        } catch (error) {
            console.error("Avatar upload error:", error.response?.data || error.message);
            const message = error.response?.data?.msg || 'Failed to upload avatar.';
            notify(message, 'error');
        } finally {
            setIsUploading(false);
        }
    };

    const handleReturn = async (borrowingId, title) => {
        if (!token) { // Check for token
             notify('Authentication required.', 'error');
             return;
        }
        if (!window.confirm(`Are you sure you want to return the book: "${title}"?`)) return;

        try {
            // Add header manually
            await axios.post(`/return/${borrowingId}`, {}, { // Include empty data object for POST
                 headers: { 'Authorization': `Bearer ${token}` }
            });
            notify(`'${title}' returned successfully!`, 'success');
            fetchProfile(token); // Refresh history
        } catch (error) {
            console.error("Return book error:", error.response?.data || error.message);
            notify(error.response?.data?.msg || 'Failed to return book.', 'error');
        }
    };

    if (!user) return <p className="text-center text-xl mt-12 text-stone-600">Loading profile or please log in.</p>;

    return (
        <div className="container mx-auto px-4 py-8 grid lg:grid-cols-3 gap-8">
            {/* Profile Card */}
            <Card className="lg:col-span-1 h-fit">
                <div className="flex flex-col items-center">
                    <div className="relative w-32 h-32 rounded-full overflow-hidden mb-4 border-4 border-rose-500 shadow-lg">
                        <img
                            src={user.avatar_url || 'https://placehold.co/128x128/E879F9/FFFFFF?text=P'} // Pink placeholder
                            alt="User Avatar"
                            className="object-cover w-full h-full"
                            // Basic error handling for image load
                            onError={(e) => { e.target.onerror = null; e.target.src="https://placehold.co/128x128/E879F9/FFFFFF?text=Err" }}
                        />
                    </div>
                    <h3 className="text-3xl font-bold text-stone-900">{user.username}</h3>
                    <p className={`text-sm font-semibold mt-1 py-1 px-3 rounded-full ${isAdmin ? 'bg-red-100 text-red-600' : isLibrarianOrAdmin ? 'bg-rose-100 text-rose-600' : 'bg-gray-100 text-stone-600'}`}>
                        {user.role?.toUpperCase() || 'USER'} {/* Added fallback */}
                    </p>
                    <div className="mt-6 w-full space-y-2 text-stone-600">
                        <p className="flex justify-between items-center border-b pb-1">Email: <span className="font-medium text-stone-800 break-all">{user.email}</span></p>
                        <p className="flex justify-between items-center">Member Since: <span className="font-medium text-stone-800">{user.created_at ? new Date(user.created_at).toLocaleDateString() : 'N/A'}</span></p>
                        <p className="text-xs break-words pt-4 text-center text-stone-400">User ID: {user.id}</p>
                    </div>
                </div>
            </Card>

            {/* Avatar Upload and History */}
            <div className="lg:col-span-2 space-y-8">
                <Card title="Change Avatar">
                    <div className="flex flex-col sm:flex-row space-y-4 sm:space-y-0 sm:space-x-4 items-center">
                        <input type="file" onChange={handleFileChange} accept="image/*" className="block w-full text-sm text-stone-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-rose-50 file:text-rose-700 hover:file:bg-rose-100" />
                        <button
                            onClick={handleUpload}
                            disabled={!file || isUploading || !token} // Disable if no token
                            className="w-full sm:w-auto flex-shrink-0 bg-emerald-600 text-white p-3 rounded-lg font-semibold hover:bg-emerald-700 transition duration-200 shadow-md disabled:bg-emerald-400"
                        >
                            {isUploading ? 'Uploading...' : 'Save Avatar'}
                        </button>
                    </div>
                    <p className="mt-2 text-sm text-stone-500">Max file size: 2MB. Allowed types: PNG, JPG, JPEG, GIF.</p>
                </Card>

                <Card title="Borrowing History">
                    {/* Ensure borrowing_history exists before mapping */}
                    {!user.borrowing_history || user.borrowing_history.length === 0 ? (
                        <p className="text-stone-500">You haven't borrowed any books yet.</p>
                    ) : (
                        <div className="space-y-4 max-h-96 overflow-y-auto pr-2">
                            {user.borrowing_history.map((item) => (
                                <div key={item.id} className="p-4 border border-gray-100 rounded-xl shadow-sm hover:shadow-md transition duration-200 flex justify-between items-center bg-gray-50">
                                    <div>
                                        <p className="font-semibold text-stone-900">{item.book_title}</p>
                                        <p className="text-sm text-stone-600">Borrowed: {new Date(item.borrow_date).toLocaleDateString()}</p>
                                        <p className="text-sm text-red-500 font-medium">Due: {new Date(item.due_date).toLocaleDateString()}</p>
                                    </div>
                                    <div className="flex items-center space-x-3">
                                        <span className={`px-3 py-1 text-xs font-semibold rounded-full ${
                                            item.status === 'returned' ? 'bg-emerald-100 text-emerald-600' :
                                            (new Date(item.due_date) < new Date() && item.status !== 'returned' ? 'bg-red-100 text-red-600' : 'bg-yellow-100 text-yellow-600') // Check status for overdue
                                        }`}>
                                            {item.status?.toUpperCase() || 'UNKNOWN'} {/* Added fallback */}
                                        </span>
                                        {item.status === 'borrowed' && (
                                            <button
                                                onClick={() => handleReturn(item.id, item.book_title)}
                                                className="p-2 bg-rose-500 text-white rounded-full hover:bg-rose-600 transition shadow-md"
                                                title="Return Book"
                                            >
                                                <RotateCcw className="w-5 h-5" />
                                            </button>
                                        )}
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </Card>
            </div>
        </div>
    );
};


// --- BookModal Component (MOVED OUTSIDE BookManager) ---
const BookModal = ({ appState, currentBook, form, handleInputChange, handleSubmit, handleCloseModal }) => {
    // Get loading state directly from appState
    const { loading } = appState;
    return (
        <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex justify-center items-center z-50 p-4 overflow-y-auto">
            <Card title={currentBook ? "Edit Book" : "Add New Book"} className="w-full max-w-lg my-8"> {/* Added my-8 for spacing */}
                <form onSubmit={handleSubmit}>
                    <InputField label="Title" id="title" name="title" value={form.title || ''} onChange={handleInputChange} required />
                    <InputField label="Author" id="author" name="author" value={form.author || ''} onChange={handleInputChange} required />
                    <InputField label="ISBN" id="isbn" name="isbn" value={form.isbn || ''} onChange={handleInputChange} />
                    <div className="grid grid-cols-2 gap-4">
                        <InputField label="Year" id="published_year" name="published_year" type="number" value={form.published_year || ''} onChange={handleInputChange} />
                        <InputField label="Genre" id="genre" name="genre" value={form.genre || ''} onChange={handleInputChange} />
                    </div>
                    <InputField label="Stock" id="stock" name="stock" type="number" value={form.stock || ''} onChange={handleInputChange} required min="0" /> {/* Added min="0" */}

                    <div className="flex justify-end space-x-4 mt-6">
                        <button type="button" onClick={handleCloseModal} className="px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-100 transition">Cancel</button>
                        <button type="submit" disabled={loading} className="flex items-center space-x-2 px-4 py-2 bg-rose-600 text-white rounded-lg hover:bg-rose-700 transition disabled:bg-rose-400">
                            <Save className="w-5 h-5" />
                            <span>{loading ? 'Saving...' : 'Save Book'}</span>
                        </button>
                    </div>
                </form>
            </Card>
        </div>
    );
};


// --- Book Management Component (Admin/Librarian) ---
const BookManager = ({ appState, books, fetchBooks }) => {
  // Get token here for API calls within BookManager
  const { isLibrarianOrAdmin, isAdmin, notify, setLoading, loading, token } = appState;
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentBook, setCurrentBook] = useState(null);
  // Keep form state local to BookManager
  const [form, setForm] = useState({ title: '', author: '', isbn: '', published_year: '', stock: 1, genre: '' });

  // Update local form state when currentBook changes
  useEffect(() => {
    if (currentBook) {
      setForm({
          title: currentBook.title || '',
          author: currentBook.author || '',
          isbn: currentBook.isbn || '',
          published_year: currentBook.published_year || '',
          stock: currentBook.stock || 0,
          genre: currentBook.genre || ''
      });
    } else {
      // Reset form when adding a new book
      setForm({ title: '', author: '', isbn: '', published_year: '', stock: 1, genre: '' });
    }
  }, [currentBook]);

  if (!isLibrarianOrAdmin) {
    return <p className="text-center text-red-500 text-xl mt-12">Authorization Required: Only Librarians and Admins can access Book Management.</p>;
  }

  const handleOpenModal = (book = null) => {
    setCurrentBook(book);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setCurrentBook(null); // Reset currentBook as well
  };

  const handleInputChange = (e) => {
    const { name, value, type } = e.target;
    setForm(prev => ({
      ...prev,
      // Handle number inputs specifically
      [name]: type === 'number' ? (parseInt(value) || 0) : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!token) {
        notify('Authentication required.', 'error');
        return;
    }
    setLoading(true);

    try {
        // Prepare headers
        const config = { headers: { 'Authorization': `Bearer ${token}` } };
        if (currentBook) {
            // Update book
            await axios.put(`/books/${currentBook.id}`, form, config);
            notify('Book updated successfully!', 'success');
        } else {
            // Create book
            await axios.post('/books', form, config);
            notify('Book added successfully!', 'success');
        }
        fetchBooks(token); // Refresh book list after saving, pass token
        handleCloseModal();
    } catch (error) {
        console.error("Save book error:", error.response?.data || error.message);
        notify(error.response?.data?.msg || 'Error saving book data.', 'error');
    } finally {
        setLoading(false);
    }
  };

  const handleDelete = async (bookId, title) => {
    if (!isAdmin) {
      notify('Only Admins can delete books.', 'error');
      return;
    }
    if (!token) { // Check for token
        notify('Authentication required.', 'error');
        return;
    }
    if (!window.confirm(`Are you sure you want to delete the book: "${title}"?`)) return;

    try {
        setLoading(true);
        // Add header manually
        await axios.delete(`/books/${bookId}`, {
            headers: { 'Authorization': `Bearer ${token}` }
        });
        notify('Book deleted successfully!', 'success');
        fetchBooks(token); // Refresh book list after deleting, pass token
    } catch (error) {
        console.error("Delete book error:", error.response?.data || error.message);
        notify(error.response?.data?.msg || 'Error deleting book.', 'error');
    } finally {
        setLoading(false);
    }
  };

  return (
    <>
      {/* Render BookModal conditionally, passing necessary props */}
      {isModalOpen && (
          <BookModal
              appState={appState}
              currentBook={currentBook}
              form={form}
              handleInputChange={handleInputChange}
              handleSubmit={handleSubmit}
              handleCloseModal={handleCloseModal}
          />
      )}
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-3xl font-bold text-stone-800">Book Management</h2>
        <button
          onClick={() => handleOpenModal()}
          className="flex items-center space-x-2 px-4 py-2 bg-rose-600 text-white rounded-lg font-semibold hover:bg-rose-700 transition shadow-md"
        >
          <Plus className="w-5 h-5" />
          <span>Add New Book</span>
        </button>
      </div>

      {/* Book Table */}
       <div className="overflow-x-auto bg-white rounded-xl shadow-2xl">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-stone-500 uppercase tracking-wider">Title</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-stone-500 uppercase tracking-wider">Author</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-stone-500 uppercase tracking-wider">ISBN</th>
              <th className="px-6 py-3 text-center text-xs font-medium text-stone-500 uppercase tracking-wider">Stock</th>
              <th className="px-6 py-3 text-center text-xs font-medium text-stone-500 uppercase tracking-wider">Available</th>
              <th className="px-6 py-3 text-center text-xs font-medium text-stone-500 uppercase tracking-wider">Actions</th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
             {/* Ensure books is an array before mapping */}
            {Array.isArray(books) && books.map((book) => (
              <tr key={book.id} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-stone-900">{book.title}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-stone-500">{book.author}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-stone-500">{book.isbn || 'N/A'}</td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-center text-stone-900">{book.stock}</td>
                <td className={`px-6 py-4 whitespace-nowrap text-sm text-center font-bold ${book.available_copies === 0 ? 'text-red-500' : 'text-emerald-600'}`}>
                  {book.available_copies}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium flex justify-center space-x-3">
                  <button onClick={() => handleOpenModal(book)} className="text-rose-600 hover:text-rose-900 p-1 rounded-full hover:bg-rose-50 transition" title="Edit Book"><Settings className="w-5 h-5" /></button>
                  <button onClick={() => handleDelete(book.id, book.title)} disabled={!isAdmin || loading} className={`p-1 rounded-full transition ${isAdmin ? 'text-red-600 hover:text-red-900 hover:bg-red-50' : 'text-stone-400 cursor-not-allowed'} ${loading ? 'opacity-50' : ''}`} title="Delete Book (Admin Only)"><Trash2 className="w-5 h-5" /></button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
};


// --- Dashboard Component (Book List for all users) ---
const DashboardPage = ({ appState }) => {
  // Extract token here to pass down
  const { isLibrarianOrAdmin, notify, loading, setLoading, token } = appState;
  const [books, setBooks] = useState([]);
  const [error, setError] = useState(null);

  // fetchBooks now accepts the token as an argument
  const fetchBooks = useCallback(async (authToken) => {
    if (!authToken) {
        console.log("fetchBooks: No token provided.");
        setError("Authentication required to view books.");
        notify("Authentication required.", "error");
        setBooks([]); // Clear books if no token
        return;
    }
    setLoading(true);
    setError(null);
    try {
      console.log('Fetching books with token...');
      // Add header manually
      const res = await axios.get('/books', {
          headers: { 'Authorization': `Bearer ${authToken}` }
      });
      console.log('Books response:', res.data);
      setBooks(Array.isArray(res.data) ? res.data : []);
    } catch (error) {
      console.error('Error fetching books:', error.response?.data || error.message);
      const errorMessage = error.response?.data?.msg || 'Failed to load books.';
      setError(errorMessage);
      notify(errorMessage, 'error');
      // Handle auth errors specifically
      if (error.response && (error.response.status === 401 || error.response.status === 422)) {
          // Optional: Call logout() if token is invalid
          // appState.logout(); // Uncomment if you want auto-logout on failed book fetch
      }
    } finally {
      setLoading(false);
    }
  }, [setLoading, notify]); // Dependencies: only stable functions

  // useEffect to fetch books when the component mounts or token changes
  useEffect(() => {
    console.log('DashboardPage useEffect: Token =', token);
    fetchBooks(token); // Pass the current token to fetchBooks
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [token]); // Re-fetch ONLY when token changes, fetchBooks dependency removed

  const handleBorrow = async (bookId, title) => {
    if (!token) {
        notify('Please log in to borrow a book.', 'error');
        return;
    }
    if (!window.confirm(`Confirm borrowing: "${title}"?`)) return;

    try {
        setLoading(true);
        // Add header manually
        const res = await axios.post(`/borrow/${bookId}`, {}, { // Include empty data object
             headers: { 'Authorization': `Bearer ${token}` }
        });
        notify(res.data.msg, 'success');
        fetchBooks(token); // Refresh book list, pass token
    } catch (error) {
        console.error("Borrow book error:", error.response?.data || error.message);
        notify(error.response?.data?.msg || 'Failed to borrow book.', 'error');
    } finally {
        setLoading(false);
    }
  };

    // Render loading state
    if (loading) {
      return <p className="text-center text-xl mt-12 text-rose-600">Loading books...</p>;
    }

    // Render error state
    if (error && !token) { // Show specific message if not logged in
        return (
            <div className="text-center py-8">
                <p className="text-xl text-red-600">Please log in to view books.</p>
                 <button
                    onClick={() => appState.setRoute('login')} // Use appState from props
                    className="mt-4 px-4 py-2 bg-rose-600 text-white rounded hover:bg-rose-700"
                >
                    Go to Login
                </button>
            </div>
        );
    } else if (error) { // General error if logged in but fetch failed
         return (
            <div className="text-center py-8">
                <p className="text-xl text-red-600">{error}</p>
                <button
                    onClick={() => fetchBooks(token)} // Retry with current token
                    className="mt-4 px-4 py-2 bg-rose-600 text-white rounded hover:bg-rose-700"
                >
                    Retry Loading Books
                </button>
            </div>
        );
    }

  // Render main content
  return (
    <div className="container mx-auto px-4 py-8">
      {/* Conditionally render BookManager if user has appropriate role */}
      {isLibrarianOrAdmin && <BookManager appState={appState} books={books} fetchBooks={fetchBooks} />}

      <h2 className={`text-3xl font-bold text-stone-800 ${isLibrarianOrAdmin ? 'mt-12 border-t pt-8' : 'mt-8'}`}> {/* Adjusted margin */}
        Library Catalog
      </h2>

      {/* Ensure books is an array before mapping */}
      {!Array.isArray(books) || books.length === 0 ? (
        <p className="text-center text-stone-600 mt-8">No books available in the catalog.</p>
      ) : (
        <div className="mt-6 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {books.map((book) => (
            <Card key={book.id} className="flex flex-col justify-between">
              <div> {/* Wrapper for top content */}
                <h3 className="text-xl font-semibold text-rose-700">{book.title}</h3>
                <p className="text-sm text-stone-600 italic">by {book.author}</p>
                <p className="text-xs mt-2 bg-stone-100 text-stone-700 px-2 py-1 rounded-md w-fit"> {/* Changed gray to stone */}
                  Genre: {book.genre || 'N/A'}
                </p>
              </div>
              <div className="mt-4 pt-4 border-t">
                <p className="text-sm font-medium text-stone-800">
                  Available: <span className={book.available_copies > 0 ? 'text-emerald-600' : 'text-red-500'}>
                    {book.available_copies} / {book.stock}
                  </span>
                </p>
                <button
                  onClick={() => handleBorrow(book.id, book.title)}
                  disabled={book.available_copies === 0 || loading || !token} // Disable if no token
                  className="mt-3 w-full p-2 bg-rose-600 text-white rounded-lg font-medium hover:bg-rose-700 transition duration-150 disabled:bg-gray-400 disabled:cursor-not-allowed"
                >
                  {token ? (book.available_copies === 0 ? 'Out of Stock' : 'Borrow Book') : 'Log in to Borrow'}
                </button>
              </div>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
};


