// --- Main App Component ---
const App = () => {
  const appState = useAppState();
  const { route, token, user, isAdmin } = appState; // Destructure needed state

  // Centralized Routing Logic
  const renderContent = useMemo(() => {
    // Routes accessible only when logged out
    if (!token) {
        switch (route) {
            case 'login': return <LoginPage appState={appState} />;
            case 'register': return <RegisterPage appState={appState} />;
            case 'home':
            default: return <HomePage />;
        }
    }

    // Routes accessible only when logged in
    switch (route) {
      case 'profile':
        return <ProfilePage appState={appState} />;
      case 'dashboard': // This might show Admin specific or general user dashboard
         return user?.role === 'admin'
             ? <AdminDashboard appState={appState} />
             : <DashboardPage appState={appState} />; // Default dashboard for non-admins
      case 'books': // Explicit route for managing/viewing books
          return <DashboardPage appState={appState} />; // DashboardPage handles roles internally

      // Admin-only routes
      case 'users':
          return isAdmin ? <UserManagementPage /> : <HomePage />; // Redirect non-admins
      case 'settings':
          return isAdmin ? <SettingsPage /> : <HomePage />; // Redirect non-admins

      // Default route when logged in (if route doesn't match above)
      case 'home': // Allow going home even when logged in
      default:
         // If user is admin, default to admin dashboard, else default to book list
         return user?.role === 'admin'
             ? <AdminDashboard appState={appState} />
             : <DashboardPage appState={appState} />;
    }
  }, [route, token, user, isAdmin, appState]); // Dependencies updated

  return (
    // Changed bg-gray-50 to bg-stone-50
    <div className="min-h-screen bg-stone-50 font-sans">
      <Header user={user} setRoute={appState.setRoute} logout={appState.logout} />
      <main className="pb-16">
        {renderContent}
      </main>
      <Notification message={appState.message} />
    </div>
  );
};


