// --- Main App Component ---

const App = () => {
  const appState = useAppState();
  const { route } = appState;

  const renderContent = useMemo(() => {
    switch (route) {
      case 'home':
        return <HomePage />;
      case 'login':
        return <LoginPage appState={appState} />;
      case 'register':
        return <RegisterPage appState={appState} />;
      case 'dashboard':
        return <DashboardPage appState={appState} />;
      case 'profile':
        return <ProfilePage appState={appState} />;
      default:
        return <HomePage />;
    }
  }, [route, appState]);

  return (
    <div className="min-h-screen bg-gray-100">
      <Notification message={appState.message} />
      <Header user={appState.user} setRoute={appState.setRoute} logout={appState.logout} />
      <main className="container mx-auto px-4 py-8">
        {renderContent}
      </main>
    </div>
  );
};

export default App;
